export const rightToc = [{
  value: '先决条件',
  id: '先决条件',
  children: []
}, {
  value: '升级大纲',
  id: '升级大纲',
  children: [{
    value: '备份运行 Rancher Server 的 Kubernetes 集群',
    id: '备份运行-rancher-server-的-kubernetes-集群',
    children: []
  }, {
    value: '更新 Helm chart 仓库',
    id: '更新-helm-chart-仓库',
    children: []
  }, {
    value: '升级 Rancher',
    id: '升级-rancher',
    children: []
  }, {
    value: '验证升级',
    id: '验证升级',
