  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '恢复备份-Docker安装',
	description: 'description',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
