		'rancher 2.0',
		'rancher2.0 中文教程',
		'备份与恢复',
		'rancher2.5',
		'恢复备份-Docker安装'
	]
};
export const metadata = {
  "id": "rancher2/backups/2.5/docker-installs/docker-restores/_index",
  "title": "恢复备份-Docker安装",
  "description": "description",
  "source": "@site/docs/rancher2/backups/2.5/docker-installs/docker-restores/_index.md",
  "permalink": "/docs/rancher2/backups/2.5/docker-installs/docker-restores/_index",
