  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '创建一次性快照'
};
export const metadata = {
  "id": "rke/etcd-snapshots/one-time-snapshots/_index",
  "title": "创建一次性快照",
  "description": "## 概述",
  "source": "@site/docs/rke/etcd-snapshots/one-time-snapshots/_index.md",
  "permalink": "/docs/rke/etcd-snapshots/one-time-snapshots/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rke/etcd-snapshots/one-time-snapshots/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '概述',
  id: '概述',
  children: []
}, {
  value: 'RKE v0.2.0 或以上的版本',
  id: 'rke-v020-或以上的版本',
  children: [{
    value: 'rke etcd snapshot-save 命令的可配置参数',
    id: 'rke-etcd-snapshot-save-命令的可配置参数',
    children: []
  }, {
    value: '使用自定义 CA 证书认证 S3',
    id: '使用自定义-ca-证书认证-s3',
    children: []
  }, {
    value: '使用 IAM 认证 S3 并储存节点快照',
    id: '使用-iam-认证-s3-并储存节点快照',
    children: []
  }]
}, {
  value: 'RKE v0.2.0 之前的版本',
  id: 'rke-v020-之前的版本',
  children: [{
    value: 'rke etcd snapshot-save 命令的可参数',
    id: 'rke-etcd-snapshot-save-命令的可参数',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "概述"
    }}>{`概述`}</h2>
    <p>{`v0.2.0 或以上的版本和 v0.2.0 之前的版本创建一次性快照的方式不同，请阅读下文对应的章节获取创建一次性快照的信息。`}</p>
    <h2 {...{
      "id": "rke-v020-或以上的版本"
    }}>{`RKE v0.2.0 或以上的版本`}</h2>
    <p>{`打开命令行工具，输入`}<inlineCode parentName="p">{`rke etcd snapshot-save`}</inlineCode>{`命令，运行后即可保存 cluster config 文件内每个 etcd 节点的快照。RKE 会将节点快照保存在`}<inlineCode parentName="p">{`/opt/rke/etcd-snapshots`}</inlineCode>{`路径下。运行上述命令时，RKE 会创建一个用于备份快照的容器。完成备份后，RKE 会删除该容器。您可以将一次性快照适配 S3 的后端主机。具体过程如下：`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`首先，运行以下命令，在本地创建一个一次性快照：`}</p>
        <pre parentName="li"><code parentName="pre" {...{}}>{`rke etcd snapshot-save --config cluster.yml --name snapshot-name
`}</code></pre>
        <p parentName="li"><strong parentName="p">{`结果：`}</strong>{` 创建了一个快照，保存在 `}<inlineCode parentName="p">{`/opt/rke/etcd-snapshots`}</inlineCode>{`路径下。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`然后，运行以下命令，将这个快照保存到 S3。`}</p>
        <pre parentName="li"><code parentName="pre" {...{}}>{`rke etcd snapshot-save \\
--config cluster.yml \\
--name snapshot-name \\
--s3 \\
--access-key S3_ACCESS_KEY \\
--secret-key S3_SECRET_KEY \\
--bucket-name s3-bucket-name \\
--folder s3-folder-name \\ # Optional - Available as of v0.3.0
--s3-endpoint s3.amazonaws.com
`}</code></pre>
        <p parentName="li"><strong parentName="p">{`结果：`}</strong>{` 保存在 `}<inlineCode parentName="p">{`/opt/rke/etcd-snapshots`}</inlineCode>{`路径下的快照已经上传至 S3。`}</p>
      </li>
    </ol>
    <h3 {...{
      "id": "rke-etcd-snapshot-save-命令的可配置参数"
    }}>{`rke etcd snapshot-save 命令的可配置参数`}</h3>
    <p>{`创建一次性快照时，可配置的参数如下表所示：`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`描述`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`S3 相关参数`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--name`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`指定快照名称`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--config`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`指定 YAML 文件名称，如果不指定，则会使用`}<inlineCode parentName="td">{`cluster.yml`}</inlineCode>{`文件`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--s3`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`启用 S3 存储服务备份节点快照`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--s3-endpoint`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`指定 S3 端点 URL 地址，默认值为 `}<strong parentName="td">{`s3.amazonaws.com`}</strong></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--s3-endpoint-ca`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`指定 CA 证书文件的路径连接自定义 S3 端点（可选）， RKE v0.2.5 及以上版本可用`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--access-key`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`S3 的 accessKey`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--secret-key`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`S3 的 secretKey`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--bucket-name`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`S3 的 桶名称（bucket name）`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--folder`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`指定 S3 存储节点快照的文件夹（可选）， RKE v0.3.0 及以上版本可用`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--region`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`S3 的 桶所在的区域（可选）`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--ssh-agent-auth`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><a parentName="td" {...{
              "href": "/docs/rke/config-options/_index"
            }}>{`使用 SSH_AUTH_SOCK 定义的 SSH Agent Auth`}</a></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--ignore-docker-version`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><a parentName="td" {...{
              "href": "/docs/rke/config-options/_index"
            }}>{`禁用 Docker 版本检查`}</a></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
      </tbody>
    </table>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`说明：`}</strong></p>
      <ul parentName="blockquote">
        <li parentName="ul">{`如果 AWS EC2 示例配置了 IAM 认证，则`}<inlineCode parentName="li">{`--access-key`}</inlineCode>{`和`}<inlineCode parentName="li">{`--secret-key`}</inlineCode>{`不是必填项。`}</li>
        <li parentName="ul">{`表格第三列标记为"`}{`*`}{` "的参数，是 S3 相关的参数。`}</li>
      </ul>
    </blockquote>
    <h3 {...{
      "id": "使用自定义-ca-证书认证-s3"
    }}>{`使用自定义 CA 证书认证 S3`}</h3>
    <p><em parentName="p">{`v0.2.0 或以上的版本可用`}</em></p>
    <p>{`备份快照可以被存储在自定义`}<inlineCode parentName="p">{`S3`}</inlineCode>{`备份机器，如`}<a parentName="p" {...{
        "href": "https://min.io/"
      }}>{`minio`}</a>{`上。如果 S3 Backend 使用的是自签名证书或自定义证书，需要使用`}<inlineCode parentName="p">{`--s3-endpoint-ca`}</inlineCode>{`将自定义证书验证并连接到 S3 Backend。`}</p>
    <h3 {...{
      "id": "使用-iam-认证-s3-并储存节点快照"
    }}>{`使用 IAM 认证 S3 并储存节点快照`}</h3>
    <p>{`RKE 支持使用 IAM 角色权限管理进行 S3 认证。集群的 etcd 节点必须分配有 IAM 角色，并且这个角色需要有读写 S3 存储节点快照的桶的权限。节点必须有权限访问 S3 端点。`}</p>
    <p>{`以下是`}<a parentName="p" {...{
        "href": "https://docs.aws.amazon.com/zh_cn/IAM/latest/UserGuide/reference_policies_examples_s3_rw-bucket.html"
      }}>{`IAM 策略示例代码`}</a>{`，给节点开放了在 S3 上读取和写入备份快照的权限。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "Version": "2012-10-17",
  "Statement": [
    {
      "Sid": "ListObjectsInBucket",
      "Effect": "Allow",
      "Action": ["s3:ListBucket"],
      "Resource": ["arn:aws:s3:::bucket-name"]
    },
    {
      "Sid": "AllObjectActions",
      "Effect": "Allow",
      "Action": "s3:*Object",
      "Resource": ["arn:aws:s3:::bucket-name/*"]
    }
  ]
}
`}</code></pre>
    <p>{`关于如何为应用开放访问 S3 的权限，请查看 AWS 的文档`}<a parentName="p" {...{
        "href": "https://docs.aws.amazon.com/zh_cn/IAM/latest/UserGuide/id_roles_use_switch-role-ec2.html"
      }}>{`使用 IAM 角色向在 Amazon EC2 实例上运行的应用程序授予权限`}</a>{`。`}</p>
    <h2 {...{
      "id": "rke-v020-之前的版本"
    }}>{`RKE v0.2.0 之前的版本`}</h2>
    <p>{`打开命令行工具，输入`}<inlineCode parentName="p">{`rke etcd snapshot-save`}</inlineCode>{`命令，运行后即可保存 cluster config 文件内每个 etcd 节点的快照。`}</p>
    <p>{`运行上述命令时，RKE 会创建一个用于备份快照的容器。完成备份后，RKE 会删除该容器。`}</p>
    <p>{`RKE 会为证书生成备份，在同一路径下将证书保存为`}<inlineCode parentName="p">{`pki.bundle.tar.gz`}</inlineCode>{`文件。恢复集群时，会用到快照和 pki 文件。`}</p>
    <p>{`运行以下命令，在本地创建一次性快照：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{` rke etcd snapshot-save --config cluster.yml --name snapshot-name
`}</code></pre>
    <p><strong parentName="p">{`结果：`}</strong>{` RKE 会将节点快照保存在`}<inlineCode parentName="p">{`/opt/rke/etcd-snapshots`}</inlineCode>{`路径下。`}</p>
    <h3 {...{
      "id": "rke-etcd-snapshot-save-命令的可参数"
    }}>{`rke etcd snapshot-save 命令的可参数`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--name`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`指定快照名称`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--config`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`指定 YAML 文件名称，如果不指定，则会使用`}<inlineCode parentName="td">{`cluster.yml`}</inlineCode>{`文件 `}{`[$RKE_CONFIG]`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--ssh-agent-auth`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><a parentName="td" {...{
              "href": "/docs/rke/config-options/_index"
            }}>{`使用 SSH_AUTH_SOCK 定义的 SSH Agent Auth`}</a></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--ignore-docker-version`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><a parentName="td" {...{
              "href": "/docs/rke/config-options/_index"
            }}>{`禁用 Docker 版本检查`}</a></td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
