  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '基于角色的访问控制',
	description: '本文介绍了 RBAC 在 Rancher 监控中起到的作用。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
