  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '自定义证书'
};
export const metadata = {
  "id": "rke/installation/certs/_index",
  "title": "自定义证书",
  "description": "_v0.2.0 以上可用_",
  "source": "@site/docs/rke/installation/certs/_index.md",
  "permalink": "/docs/rke/installation/certs/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rke/installation/certs/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '概述',
  id: '概述',
  children: []
}, {
  value: '如何使用自定义证书',
  id: '如何使用自定义证书',
  children: []
}, {
  value: '证书和密钥清单',
  id: '证书和密钥清单',
  children: []
}, {
  value: '生成证书签名请求和密钥',
  id: '生成证书签名请求和密钥',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><em parentName="p">{`v0.2.0 以上可用`}</em></p>
    <h2 {...{
      "id": "概述"
    }}>{`概述`}</h2>
    <p>{`默认情况下，Kubernetes 集群需要用到证书，而 RKE 会自动为所有集群组件生成证书。您也可以使用`}<a parentName="p" {...{
        "href": "/docs/rke/installation/certs/_index"
      }}>{`自定义证书`}</a>{`。`}</p>
    <p>{`使用`}<a parentName="p" {...{
        "href": "/docs/rke/installation/_index#%E4%BD%BF%E7%94%A8-RKE-%E9%83%A8%E7%BD%B2-Kubernetes"
      }}>{`RKE 部署 Kubernetes`}</a>{`时，可以在`}<inlineCode parentName="p">{`rke up`}</inlineCode>{`命令后面添加额外的选项，命令 RKE 使用自定义证书部署 Kubernetes。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`选项`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--custom-certs`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果您没有修改保存证书的路径，RKE 会使用默认路径`}<inlineCode parentName="td">{`/cluster_certs`}</inlineCode>{`保存证书，自定义证书会保存在该路径。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`--cert-dir`}</inlineCode>{` value`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果您的自定义证书保存在其他路径下，可以使用这个选项将保存证书的默认路径改为您保存证书的路径。`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "如何使用自定义证书"
    }}>{`如何使用自定义证书`}</h2>
    <p>{`以下是分别使用两种选项的代码示例：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`# 如果您没有修改保存证书的路径，RKE 会使用默认路径\`/cluster_certs\`保存证书，自定义证书会保存在该路径
rke up --custom-certs
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`# 如果您的自定义证书保存在其他路径下，可以使用这个选项将保存证书的默认路径改为您保存证书的路径
rke up --custom-certs --cert-dir ~/my/own/certs
`}</code></pre>
    <h2 {...{
      "id": "证书和密钥清单"
    }}>{`证书和密钥清单`}</h2>
    <p>{`无论您使用默认路径还是其他路径保存证书，都要确保该路径下已有以下证书和密钥：`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`名称`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`证书`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`密钥`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Master CA`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-ca.pem`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`N/A`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Kube API`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-apiserver.pem`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-apiserver-key.pem`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Kube Controller Manager`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-controller-manager.pem`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-controller-manager-key.pem`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Kube Scheduler`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-scheduler.pem`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-scheduler-key.pem`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Kube Proxy`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-proxy.pem`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-proxy-key.pem`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Kube Admin`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-admin.pem`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-admin-key.pem`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Kube Node`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-node.pem`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-node-key.pem`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Apiserver Proxy Client`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-apiserver-proxy-client.pem`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-apiserver-proxy-client-key.pem`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Etcd Nodes`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-etcd-x-x-x-x.pem`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-etcd-x-x-x-x-key.pem`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Kube Api Request Header CA`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-apiserver-requestheader-ca.pem`}{`*`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-apiserver-requestheader-ca-key.pem`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Service Account Token`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`N/A`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-service-account-token-key.pem`}</td>
        </tr>
      </tbody>
    </table>
    <p>{`*`}{`：和 kube-ca.pem 相同。`}</p>
    <h2 {...{
      "id": "生成证书签名请求和密钥"
    }}>{`生成证书签名请求和密钥`}</h2>
    <p>{`如果您想使用证书签发机构（Certificate Authority，简称 CA）创建和签发证书，您可以使用 RKE 命令`}<inlineCode parentName="p">{`rke cert generate-csr`}</inlineCode>{`，创建证书签发请求（CSR）和密钥。`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`参考`}<a parentName="p" {...{
            "href": "/docs/rke/config-options/nodes/_index"
          }}>{`节点信息`}</a>{`，配置`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`运行`}<inlineCode parentName="p">{`rke cert generate-csr`}</inlineCode>{`命令，生成`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`文件中提及的节点需要的证书。默认情况下，RKE 会将 CSR 和密钥保存在`}<inlineCode parentName="p">{`./cluster_certs`}</inlineCode>{`路径。如果您需要将它们保存在其他路径，请在命令后添加使用 `}<inlineCode parentName="p">{`--cert-dir <PathName>`}</inlineCode>{`选项，将`}<inlineCode parentName="p">{`<PathName>`}</inlineCode>{`替换成其他路径名称。`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-shell"
          }}>{`rke cert generate-csr
INFO[0000] Generating Kubernetes cluster CSR certificates
INFO[0000] [certificates] Generating Kubernetes API server csr
INFO[0000] [certificates] Generating Kube Controller csr
INFO[0000] [certificates] Generating Kube Scheduler csr
INFO[0000] [certificates] Generating Kube Proxy csr
INFO[0001] [certificates] Generating Node csr and key
INFO[0001] [certificates] Generating admin csr and kubeconfig
INFO[0001] [certificates] Generating Kubernetes API server proxy client csr
INFO[0001] [certificates] Generating etcd-x.x.x.x csr and key
INFO[0001] Successfully Deployed certificates at [./cluster_certs]
`}</code></pre>
      </li>
      <li parentName="ol">
        <p parentName="li">{`运行以下命令，生成`}<inlineCode parentName="p">{`kube-service-account-token-key.pem`}</inlineCode>{`密钥。`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-shell"
          }}>{`openssl req -x509 -nodes -days 365 -newkey rsa:2048 -keyout ./cluster_certs/kube-service-account-token-key.pem -out ./cluster_certs/kube-service-account-token.pem
`}</code></pre>
      </li>
    </ol>
    <p><strong parentName="p">{`结果：`}</strong>{` 生成了多个 CSR 和密钥。它们被部署在您指定的路径。如果您没有指定路径，它们则会被部署在`}<inlineCode parentName="p">{`./cluster_certs`}</inlineCode>{`。CSR 文件中包含了证书需要用到的 Alternative DNS 地址和 IP Names。您可以使用它们给证书颁发机构颁发的证书签名。完成此过程后，RKE 可以使用这些自定义证书。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`tree cluster_certs

cluster_certs
├── kube-admin-csr.pem
├── kube-admin-key.pem
├── kube-apiserver-csr.pem
├── kube-apiserver-key.pem
├── kube-apiserver-proxy-client-csr.pem
├── kube-apiserver-proxy-client-key.pem
├── kube-controller-manager-csr.pem
├── kube-controller-manager-key.pem
├── kube-etcd-x-x-x-x-csr.pem
├── kube-etcd-x-x-x-x-key.pem
├── kube-node-csr.pem
├── kube-node-key.pem
├── kube-proxy-csr.pem
├── kube-proxy-key.pem
├── kube-scheduler-csr.pem
├── kube-service-account-token-key.pem
├── kube-service-account-token.pem
└── kube-scheduler-key.pem

0 directories, 18 files

`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
