  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '适配器与MQTT集成',
	description: 'Octopus 提供了两种的方法与MQTT集成',
	keywords: [
		'Octopus中文文档',
		'Octopus 中文文档',
		'边缘计算',
		'IOT',
		'edge computing',
		'Octopus中文',
		'Octopus 中文',
		'Octopus',
		'Octopus教程',
		'Octopus中国',
		'rancher',
		'Octopus 中文教程',
		'适配器',
		'适配器与MQTT集成'
	]
};
export const metadata = {
  "id": "octopus/adaptors/mqtt-extension/_index",
  "title": "适配器与MQTT集成",
  "description": "Octopus 提供了两种的方法与MQTT集成",
  "source": "@site/docs/octopus/adaptors/mqtt-extension/_index.md",
  "permalink": "/docs/octopus/adaptors/mqtt-extension/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/octopus/adaptors/mqtt-extension/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
