  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '节点管理'
};
export const metadata = {
  "id": "rke/managing-clusters/_index",
  "title": "节点管理",
  "description": "## 添加或删除节点",
  "source": "@site/docs/rke/managing-clusters/_index.md",
  "permalink": "/docs/rke/managing-clusters/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rke/managing-clusters/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '添加或删除节点',
  id: '添加或删除节点',
  children: []
}, {
  value: '添加或删除 worker 节点。',
  id: '添加或删除-worker-节点。',
  children: []
}, {
  value: '移除节点中的 Kubernetes 组件',
  id: '移除节点中的-kubernetes-组件',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "添加或删除节点"
    }}>{`添加或删除节点`}</h2>
    <p>{`RKE 支持为 worker 和 controlplane 主机添加或删除`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/nodes/_index"
      }}>{`节点`}</a>{`。`}</p>
    <p>{`您可以通过修改`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`文件的内容，添加额外的节点，并指定它们在 Kubernetes 集群中的角色；或从`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`中的节点列表中删除节点信息，以达到删除节点的目的。`}</p>
    <h2 {...{
      "id": "添加或删除-worker-节点。"
    }}>{`添加或删除 worker 节点。`}</h2>
    <p>{`通过运行`}<inlineCode parentName="p">{`rke up --update-only`}</inlineCode>{`，您可以运行`}<inlineCode parentName="p">{`rke up --update-only`}</inlineCode>{`命令，只添加或删除工作节点。这将会忽略除了`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`中的工作节点以外的其他内容。`}</p>
    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`注意`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`使用`}<inlineCode parentName="p">{`--update-only`}</inlineCode>{`添加或删除 worker 节点时，可能会触发`}<a parentName="p" {...{
            "href": "/docs/rke/config-options/add-ons/_index"
          }}>{`插件`}</a>{`或其他组件的重新部署或更新。`}</p></div></div>
    <h2 {...{
      "id": "移除节点中的-kubernetes-组件"
    }}>{`移除节点中的 Kubernetes 组件`}</h2>
    <p>{`您可以使用`}<inlineCode parentName="p">{`rke remove`}</inlineCode>{`命令从节点中移除 Kubernetes 组件。`}</p>
    <div {...{
      "className": "admonition admonition-warning alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`警告`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`这个命令是`}<strong parentName="p">{`不可逆`}</strong>{`的，这个命令会毁坏 Kubernetes 集群，包括 S3 上的 etcd 集群快照。如果发生灾难，将无法访问您的集群，请参考`}<a parentName="p" {...{
            "href": "/docs/rke/etcd-snapshots/_index"
          }}>{`从快照恢复集群`}</a>{`的流程。`}</p></div></div>
    <ul>
      <li parentName="ul">
        <p parentName="li"><inlineCode parentName="p">{`rke remove`}</inlineCode>{`命令会删除`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`中的每个节点上面的 Kubernetes 组件，包括：`}</p>
        <ul parentName="li">
          <li parentName="ul"><inlineCode parentName="li">{`etcd`}</inlineCode></li>
          <li parentName="ul"><inlineCode parentName="li">{`kube-apiserver`}</inlineCode></li>
          <li parentName="ul"><inlineCode parentName="li">{`kube-controller-manager`}</inlineCode></li>
          <li parentName="ul"><inlineCode parentName="li">{`kubelet`}</inlineCode></li>
          <li parentName="ul"><inlineCode parentName="li">{`kube-proxy`}</inlineCode></li>
          <li parentName="ul"><inlineCode parentName="li">{`nginx-proxy`}</inlineCode></li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li"><inlineCode parentName="p">{`rke remove`}</inlineCode>{`命令会删除集群的 etcd 快照，包括：`}</p>
        <ul parentName="li">
          <li parentName="ul">{`本地快照`}</li>
          <li parentName="ul">{`存储在 S3 上的快照`}</li>
        </ul>
      </li>
    </ul>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{` `}<inlineCode parentName="p">{`rke remove`}</inlineCode>{`命令不会从节点上删除 Pods。如果节点被重复使用，那么在创建新的 Kubernetes 集群时，将自动删除 pod。`}</p>
    </blockquote>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`rke remove`}</inlineCode>{`命令会从服务留下的目录中清理每个主机。`}<ul parentName="li">
          <li parentName="ul">{`/etc/kubernetes/ssl`}</li>
          <li parentName="ul">{`/var/lib/etcd`}</li>
          <li parentName="ul">{`/etc/cni`}</li>
          <li parentName="ul">{`/opt/cni`}</li>
          <li parentName="ul">{`/var/run/calico`}</li>
        </ul></li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
