  export const frontMatter = {
	title: '2、同步镜像到私有镜像仓库',
	description: '本节描述如何设置您的私有镜像库，以便在安装 Rancher 时，Rancher 从此私有镜像库拉取所有必需的镜像。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'安装指南',
		'资料、参考和高级选项',
		'Rancher 高可用 Helm2 离线安装',
		'同步镜像到私有镜像仓库'
	]
};
export const metadata = {
  "id": "rancher2/installation/options/air-gap-helm2/populate-private-registry/_index",
  "title": "2、同步镜像到私有镜像仓库",
  "description": "本节描述如何设置您的私有镜像库，以便在安装 Rancher 时，Rancher 从此私有镜像库拉取所有必需的镜像。",
  "source": "@site/docs/rancher2/installation/options/air-gap-helm2/populate-private-registry/_index.md",
  "permalink": "/docs/rancher2/installation/options/air-gap-helm2/populate-private-registry/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/installation/options/air-gap-helm2/populate-private-registry/_index.md",
