export const rightToc = [{
  value: '只需要通过 Rancher 创建 Linux 集群',
  id: '只需要通过-rancher-创建-linux-集群',
  children: []
}, {
  value: '需要通过 Rancher 创建 Linux 集群 和 Windows 集群',
  id: '需要通过-rancher-创建-linux-集群-和-windows-集群',
  children: [{
    value: '同步 Windows 镜像',
    id: '同步-windows-镜像',
