  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Kubernetes 仪表盘',
	description: '本安装指南将帮助您在K3s上部署和配置Kubernetes 仪表盘',
	keywords: [
		'k3s中文文档',
		'k3s 中文文档',
		'k3s中文',
		'k3s 中文',
		'k3s',
		'k3s教程',
		'k3s中国',
		'rancher',
		'k3s 中文教程',
		'Kubernetes 仪表盘'
	]
};
export const metadata = {
  "id": "k3s/installation/kube-dashboard/_index",
  "title": "Kubernetes 仪表盘",
  "description": "本安装指南将帮助您在K3s上部署和配置Kubernetes 仪表盘",
  "source": "@site/docs/k3s/installation/kube-dashboard/_index.md",
  "permalink": "/docs/k3s/installation/kube-dashboard/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/k3s/installation/kube-dashboard/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
