export const rightToc = [{
  value: '部署 Kubernetes 仪表盘',
  id: '部署-kubernetes-仪表盘',
  children: []
}, {
  value: '仪表盘 RBAC 配置',
  id: '仪表盘-rbac-配置',
  children: []
}, {
  value: '获得 Bearer Token',
  id: '获得-bearer-token',
  children: []
}, {
  value: '本地访问仪表板',
  id: '本地访问仪表板',
  children: [{
    value: '高级：远程访问仪表板',
    id: '高级：远程访问仪表板',
    children: []
  }]
}, {
  value: '升级仪表盘',
  id: '升级仪表盘',
  children: []
}, {
  value: '删除仪表板和 admin-user 配置',
  id: '删除仪表板和-admin-user-配置',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`本安装指南将帮助您在 K3s 上部署和配置`}<a parentName="p" {...{
        "href": "https://kubernetes.io/docs/tasks/access-application-cluster/web-ui-dashboard/"
      }}>{`Kubernetes 仪表盘`}</a>{`。`}</p>
    <h2 {...{
      "id": "部署-kubernetes-仪表盘"
    }}>{`部署 Kubernetes 仪表盘`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`GITHUB_URL=https://github.com/kubernetes/dashboard/releases
VERSION_KUBE_DASHBOARD=$(curl -w '%{url_effective}' -I -L -s -S \${GITHUB_URL}/latest -o /dev/null | sed -e 's|.*/||')
sudo k3s kubectl create -f https://raw.githubusercontent.com/kubernetes/dashboard/\${VERSION_KUBE_DASHBOARD}/aio/deploy/recommended.yaml
`}</code></pre>
    <h2 {...{
      "id": "仪表盘-rbac-配置"
    }}>{`仪表盘 RBAC 配置`}</h2>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`重要：`}</strong>{` 本指南中创建的 `}<inlineCode parentName="p">{`admin-user`}</inlineCode>{` 将在仪表板中拥有管理权限。`}</p>
    </blockquote>
    <p>{`创建以下资源清单文件：`}</p>
    <p><inlineCode parentName="p">{`dashboard.admin-user.yml`}</inlineCode></p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`apiVersion: v1
kind: ServiceAccount
metadata:
  name: admin-user
  namespace: kubernetes-dashboard
`}</code></pre>
    <p><inlineCode parentName="p">{`dashboard.admin-user-role.yml`}</inlineCode></p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`apiVersion: rbac.authorization.k8s.io/v1
kind: ClusterRoleBinding
metadata:
  name: admin-user
roleRef:
  apiGroup: rbac.authorization.k8s.io
  kind: ClusterRole
  name: cluster-admin
subjects:
  - kind: ServiceAccount
    name: admin-user
    namespace: kubernetes-dashboard
`}</code></pre>
    <p>{`部署`}<inlineCode parentName="p">{`admin-user`}</inlineCode>{` 配置：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`sudo k3s kubectl create -f dashboard.admin-user.yml -f dashboard.admin-user-role.yml
`}</code></pre>
    <h2 {...{
      "id": "获得-bearer-token"
    }}>{`获得 Bearer Token`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`sudo k3s kubectl -n kubernetes-dashboard describe secret admin-user-token | grep ^token
`}</code></pre>
    <h2 {...{
      "id": "本地访问仪表板"
    }}>{`本地访问仪表板`}</h2>
    <p>{`要访问仪表板，你必须创建一个安全通道到你的 K3s 集群。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`sudo k3s kubectl proxy
`}</code></pre>
    <p>{`现在可以通过以下网址访问仪表盘：`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`http://localhost:8001/api/v1/namespaces/kubernetes-dashboard/services/https:kubernetes-dashboard:/proxy/`}</inlineCode></li>
      <li parentName="ul">{`使用`}<inlineCode parentName="li">{`admin-user`}</inlineCode>{` Bearer Token `}<inlineCode parentName="li">{`Sign In`}</inlineCode></li>
    </ul>
    <h3 {...{
      "id": "高级：远程访问仪表板"
    }}>{`高级：远程访问仪表板`}</h3>
    <p>{`请参阅仪表盘文档。使用`}<a parentName="p" {...{
        "href": "https://kubernetes.io/docs/tasks/access-application-cluster/port-forward-access-application-cluster/"
      }}>{`端口转发`}</a>{`来访问集群中的应用程序。`}</p>
    <h2 {...{
      "id": "升级仪表盘"
    }}>{`升级仪表盘`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`sudo k3s kubectl delete ns kubernetes-dashboard
GITHUB_URL=https://github.com/kubernetes/dashboard/releases
VERSION_KUBE_DASHBOARD=$(curl -w '%{url_effective}' -I -L -s -S \${GITHUB_URL}/latest -o /dev/null | sed -e 's|.*/||')
sudo k3s kubectl apply -f https://raw.githubusercontent.com/kubernetes/dashboard/\${VERSION_KUBE_DASHBOARD}/aio/deploy/recommended.yaml -f dashboard.admin-user.yml -f dashboard.admin-user-role.yml
`}</code></pre>
    <h2 {...{
      "id": "删除仪表板和-admin-user-配置"
    }}>{`删除仪表板和 admin-user 配置`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`sudo k3s kubectl delete ns kubernetes-dashboard
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
