}, {
  value: '证书选项',
  id: '证书选项',
  children: [{
    value: 'Rancher 自签名证书',
    id: 'rancher-自签名证书',
    children: []
  }, {
    value: 'Let&#39;s Encrypt',
    id: 'lets-encrypt',
    children: []
  }, {
