  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '标签'
};
export const metadata = {
  "id": "rancher1/infrastructure/cattle/labels/_index",
  "title": "标签",
  "description": "Rancher 在服务/容器和主机上使用标签来帮助管理 Rancher 的不同功能。",
  "source": "@site/docs/rancher1/infrastructure/cattle/labels/_index.md",
  "permalink": "/docs/rancher1/infrastructure/cattle/labels/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher1/infrastructure/cattle/labels/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "rancher1",
  "previous": {
    "title": "内部DNS服务",
    "permalink": "/docs/rancher1/infrastructure/cattle/internal-dns-service/_index"
  },
  "next": {
    "title": "Rancher Compose",
    "permalink": "/docs/rancher1/infrastructure/cattle/rancher-compose/_index"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Rancher Compose 标签使用指南',
  id: 'rancher-compose-标签使用指南',
  children: []
}, {
  value: '服务上的系统标签',
  id: '服务上的系统标签',
  children: []
}, {
  value: '主机标签',
  id: '主机标签',
  children: []
}, {
  value: '自动创建的主机标签',
  id: '自动创建的主机标签',
  children: []
}, {
  value: '本地 Docker 标签',
  id: '本地-docker-标签',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Rancher 在服务/容器和主机上使用标签来帮助管理 Rancher 的不同功能。`}</p>
    <h3 {...{
      "id": "rancher-compose-标签使用指南"
    }}>{`Rancher Compose 标签使用指南`}</h3>
    <p>{`标签用于帮助 Rancher 启动服务并利用 Rancher 的功能。下列的标签索引用于帮助用户使用 Rancher Compose 来创建服务。 这些标签在 UI 上有对应关系，不需要额外添加到服务上。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Key`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Value`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.sidekicks`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`服务名称`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用来定义哪些服务属于`}<a parentName="td" {...{
              "href": "/docs/rancher1/infrastructure/cattle/adding-services/_index#sidekick-%E6%9C%8D%E5%8A%A1"
            }}>{`从容器`}</a></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.loadbalancer.target.SERVICE_NAME`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`REQUEST_HOST:SOURCE_PORT/REQUEST_PATH=TARGET_PORT`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于判定 `}<a parentName="td" {...{
              "href": "/docs/rancher1/infrastructure/cattle/adding-load-balancers/_index#load-balancer-example-l7"
            }}>{`L7 Load Balancing`}</a></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.container.dns`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`true`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`服务能够使用基于 Rancher DNS 的服务发现来解析其他服务，并能被其他服务解析。 如果您需要此 DNS 服务，且网络设置为`}<inlineCode parentName="td">{`主机`}</inlineCode>{`，则此标签是必需的.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.container.hostname_override`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`容器名称`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于将容器的主机名设置为容器的名称 (例如: StackName_ServiceName_CreateIndex)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.container.start_once`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`true`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于设置容器只运行一次，并在容器为停止状态时显示`}<inlineCode parentName="td">{`active`}</inlineCode>{`状态。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.container.pull_image`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`always`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于在部署容器之前始终拉取新的镜像.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.container.requested_ip`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`IP 于`}<inlineCode parentName="td">{`10.42.0.0/16`}</inlineCode>{`的地址空间`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`允许您选择容器的特定 IP。从 v1.6.6 版本开始，服务内的容器将会使用配置的多个 IP 地址中的可用地址，直到这些地址全被占用。这些地址要用逗号隔开，例如`}<inlineCode parentName="td">{`10.42.100.100, 10.42.100.101`}</inlineCode>{`。 在 v1.6.6 之前，只有服务中的一个容器可以使用这个特定 IP。`}<strong parentName="td">{`注意:如果 IP 在主机上不可用，则容器将以随机 IP 开始.`}</strong></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.container.dns.priority`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`service_last`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`在服务域之前使用主机的 DNS 搜索路径。 保证主机将从`}<inlineCode parentName="td">{`/etc/resolv.conf`}</inlineCode>{`搜索后再对`}<inlineCode parentName="td">{`*.rancher.internal`}</inlineCode>{`搜索。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.service.selector.container`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><a parentName="td" {...{
              "href": "/docs/rancher1/infrastructure/cattle/labels/_index#%E9%80%89%E6%8B%A9%E5%99%A8%E6%A0%87%E7%AD%BE"
            }}><em parentName="a">{`Selector Label`}</em>{` Values`}</a></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于服务，以支持选择独立的容器来加入 DNS 服务。 注意:作为独立容器，任何服务操作都不会影响独立容器(即停用/删除/编辑服务，健康检查等)。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.service.selector.link`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><a parentName="td" {...{
              "href": "/docs/rancher1/infrastructure/cattle/labels/_index#%E9%80%89%E6%8B%A9%E5%99%A8%E6%A0%87%E7%AD%BE"
            }}><em parentName="a">{`Selector Label`}</em>{` Values`}</a></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于服务以允许服务基于服务标签链接到服务。 例如: Service1 具有标签`}<inlineCode parentName="td">{`io.rancher.service.selector.link:foo = bar`}</inlineCode>{`。 任何添加到 Rancher 的具有`}<inlineCode parentName="td">{`foo=bar`}</inlineCode>{`标签的服务将自动链接到 Service1。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.scheduler.global`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`true`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于设置`}<a parentName="td" {...{
              "href": "/docs/rancher1/infrastructure/cattle/scheduling/_index#%E5%85%A8%E5%B1%80%E6%9C%8D%E5%8A%A1"
            }}>{`全局服务`}</a></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.scheduler.affinity:host_label`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`主机标签的 Key Value 配对`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于根据`}<a parentName="td" {...{
              "href": "/docs/rancher1/infrastructure/cattle/scheduling/_index#%E4%BD%BF%E7%94%A8%E4%B8%BB%E6%9C%BA%E6%A0%87%E7%AD%BE%E6%9F%A5%E6%89%BE%E4%B8%BB%E6%9C%BA"
            }}>{`主机标签`}</a>{`在主机上编排容器`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.scheduler.affinity:container_label`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`容器标签的 Key Value 配对`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于根据`}<a parentName="td" {...{
              "href": "/docs/rancher1/infrastructure/cattle/scheduling/_index#%E7%94%A8%E5%AE%B9%E5%99%A8%E6%A0%87%E7%AD%BE%E6%9F%A5%E6%89%BE%E4%B8%BB%E6%9C%BA"
            }}>{`容器标签或服务名称`}</a>{`在主机上编排容器`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.scheduler.affinity:container`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`容器名称`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于根据`}<a parentName="td" {...{
              "href": "/docs/rancher1/infrastructure/cattle/scheduling/_index#%E6%9F%A5%E6%89%BE%E5%85%B7%E6%9C%89%E5%AE%B9%E5%99%A8%E5%90%8D%E7%A7%B0%E7%9A%84%E4%B8%BB%E6%9C%BA"
            }}>{`容器名称`}</a>{`在主机上安排容器`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.lb_service.target`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><a parentName="td" {...{
              "href": "#%E7%9B%AE%E6%A0%87%E6%9C%8D%E5%8A%A1%E6%A0%87%E7%AD%BE"
            }}><em parentName="a">{`Target Service Label`}</em>{` Values`}</a></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于配置负载均衡，以便将流量转发到与负载均衡位于同一主机上的容器。`}</td>
        </tr>
      </tbody>
    </table>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意:`}</strong>{` 对于以`}<inlineCode parentName="p">{`io.rancher.scheduler.affinity`}</inlineCode>{`为前缀的标签，根据您想要匹配的方式(即相等或不相等，hard 或 soft 规则)会有轻微的变化。 更多细节可以在这里找到`}<a parentName="p" {...{
          "href": "/docs/rancher1/infrastructure/cattle/scheduling/_index#table-of-scheduling-labels"
        }}>{`这里`}</a>{`.`}</p>
    </blockquote>
    <h4 {...{
      "id": "选择器标签"
    }}>{`选择器标签`}</h4>
    <p>{`使用 `}<em parentName="p">{`选择器标签`}</em>{`(即`}<inlineCode parentName="p">{`io.rancher.service.selector.link`}</inlineCode>{`, `}<inlineCode parentName="p">{`io.rancher.service.selector.container`}</inlineCode>{`)，Rancher 可以通过标签识别服务/容器，并将它们自动链接到服务。 将在以下两种情况下进行评估。 情景 1 是将 `}<em parentName="p">{`选择器标签`}</em>{` 添加到服务时。 在情景 1 中，对所有现有标签进行评估，以查看它们是否与 `}<em parentName="p">{`选择器标签`}</em>{` 匹配。 情景 2 是服务已经有 `}<em parentName="p">{`选择器标签`}</em>{` 时。 在情景 2 中，检查添加到 Rancher 的任何新服务/容器，看看它是否符合链接条件。 `}<em parentName="p">{`选择器标签`}</em>{` 可以由多个要求组成，以逗号分隔。 如果有多个要求，则必须满足所有要求，因此逗号分隔符作为`}<strong parentName="p">{` AND `}</strong>{`逻辑运算符。`}</p>
    <pre><code parentName="pre" {...{}}>{`# 其中一个容器标签必须具有一个等于\`foo1\`的键，并且值等于\`bar1\`
foo1 = bar1
# 其中一个容器标签必须具有一个等于\`foo2'的键，值不等于\`bar2\`
foo2 != bar2
＃其中一个容器标签必须有一个等于\`foo3\`的键，标签的值不重要
foo3
＃其中一个容器标签必须有一个等于\`foo4\`的键，值等于\`bar1\`或\`bar2\`
foo4 in (bar1, bar2)
＃其中一个容器标签必须有一个等于\`foo5'的键和'bar3\`或\`bar4\`以外的值
foo5 notin (bar3, bar4)
`}</code></pre>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意:`}</strong>{` 如果标签有中包含逗号的标签，则选择器将无法与标签匹配，因为 `}<em parentName="p">{`选择器标签`}</em>{` 可以匹配任何没有关联值的键。 例如: `}<inlineCode parentName="p">{`io.rancher.service.selector.link: foo=bar1,bar2`}</inlineCode>{`的标签将转换为任何服务必须具有一个标签为`}<inlineCode parentName="p">{`foo`}</inlineCode>{`的键值，并且值等于`}<inlineCode parentName="p">{`bar1`}</inlineCode>{` `}<strong parentName="p">{`和`}</strong>{`另一个带有等于`}<inlineCode parentName="p">{`bar2`}</inlineCode>{`的标签。 它不会选择一个键等于`}<inlineCode parentName="p">{`foo`}</inlineCode>{`，并且值等于`}<inlineCode parentName="p">{`bar1，bar2`}</inlineCode>{`的标签的服务。`}</p>
    </blockquote>
    <h4 {...{
      "id": "逗号分隔列表的示例"
    }}>{`逗号分隔列表的示例`}</h4>
    <pre><code parentName="pre" {...{}}>{`service1:
  labels:
    # 添加选择器标签来接收其他服务
    io.rancher.service.selector.link: hello != world, hello1 in (world1, world2), foo = bar
`}</code></pre>
    <p>{`在此示例中，将链接到`}<inlineCode parentName="p">{`service1`}</inlineCode>{`的服务需要满足以下所有条件:`}</p>
    <ul>
      <li parentName="ul">{`具有键等于`}<inlineCode parentName="li">{`hello`}</inlineCode>{`并且值不等于`}<inlineCode parentName="li">{`world`}</inlineCode>{`的标签`}</li>
      <li parentName="ul">{`具有键等于“hello1”但值可以等于`}<inlineCode parentName="li">{`world1`}</inlineCode>{`或`}<inlineCode parentName="li">{`world2`}</inlineCode>{`的标签`}</li>
      <li parentName="ul">{`具有键等于`}<inlineCode parentName="li">{`foo`}</inlineCode>{`和值等于`}<inlineCode parentName="li">{`bar`}</inlineCode>{`的标签`}</li>
    </ul>
    <p>{`以下示例，`}<inlineCode parentName="p">{`service2`}</inlineCode>{`在部署时会自动链接到`}<inlineCode parentName="p">{`service1`}</inlineCode>{`。`}</p>
    <pre><code parentName="pre" {...{}}>{`service2:
   labels:
      hello: test
      hello1: world2
      foo: bar
`}</code></pre>
    <h3 {...{
      "id": "服务上的系统标签"
    }}>{`服务上的系统标签`}</h3>
    <p>{`除了 Rancher Compose 可以使用的标签之外，Rancher 在启动服务时还会创建一系列系统标签。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Key`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.stack.name`}</inlineCode>{`/`}<inlineCode parentName="td">{`io.rancher.project.name`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`根据应用名称创建`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.stack_service.name`}</inlineCode>{`/`}<inlineCode parentName="td">{`io.rancher.project_service.name`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`根据服务名称创建`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.service.deployment.unit`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`根据部署的从容器服务创建`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.service.launch.config`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`基于从容器服务的配置创建。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.service.requested.host.id`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`根据该服务安排在哪个主机上创建`}</td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "主机标签"
    }}>{`主机标签`}</h3>
    <p><a parentName="p" {...{
        "href": "/docs/rancher1/infrastructure/hosts/_index#%E4%B8%BB%E6%9C%BA%E6%A0%87%E7%AD%BE"
      }}>{`主机标签`}</a>{` 可以在主机注册期间添加到主机，创建后可通过`}<strong parentName="p">{`编辑`}</strong>{`在主机中添加。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Key`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Value`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`io.rancher.host.external_dns_ip`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于`}<a parentName="td" {...{
              "href": "/docs/rancher1/infrastructure/cattle/external-dns-service/_index"
            }}>{`外部 DNS`}</a>{`的 IP, 例如: a.b.c.d`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`用于外部 DNS 服务，并需要对 DNS 记录进行编程`}<a parentName="td" {...{
