export const rightToc = [{
  value: '先决条件',
  id: '先决条件',
  children: []
}, {
  value: '配置步骤',
  id: '配置步骤',
  children: [{
    value: '打开 Active Directory 配置',
    id: '打开-active-directory-配置',
    children: []
  }, {
    value: '配置 Active Directory 服务器设置',
    id: '配置-active-directory-服务器设置',
    children: []
  }, {
    value: '配置用户/组架构',
    id: '配置用户组架构',
    children: []
  }, {
    value: '测试身份验证',
    id: '测试身份验证',
    children: []
  }]
}, {
  value: '附件：使用 ldapsearch 确认搜索起点和架构',
  id: '附件：使用-ldapsearch-确认搜索起点和架构',
  children: [{
    value: '确认搜索起点',
    id: '确认搜索起点',
    children: []
  }, {
    value: '确认用户架构',
    id: '确认用户架构',
    children: []
  }, {
    value: '确认组架构',
    id: '确认组架构',
