  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '为 Rancher 配置 AD FS',
	description: '在配置 Rancher 以支持 AD FS 用户之前，必须将 Rancher 添加为 AD FS 中的 Relying Party Trust。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'系统管理员指南',
		'登录认证',
		'AD FS（SAML）',
		'为 Rancher 配置 AD FS'
	]
};
export const metadata = {
  "id": "rancher2/admin-settings/authentication/microsoft-adfs/microsoft-adfs-setup/_index",
  "title": "为 Rancher 配置 AD FS",
  "description": "在配置 Rancher 以支持 AD FS 用户之前，必须将 Rancher 添加为 AD FS 中的 Relying Party Trust。",
  "source": "@site/docs/rancher2/admin-settings/authentication/microsoft-adfs/microsoft-adfs-setup/_index.md",
  "permalink": "/docs/rancher2/admin-settings/authentication/microsoft-adfs/microsoft-adfs-setup/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/admin-settings/authentication/microsoft-adfs/microsoft-adfs-setup/_index.md",
  "lastUpdatedBy": "Author",
