export const rightToc = [{
  value: '配置存储位置',
  id: '配置存储位置',
  children: [{
    value: '无默认存储位置',
    id: '无默认存储位置',
    children: []
  }, {
    value: 'S3 兼容的对象存储',
    id: 's3-兼容的对象存储',
    children: []
  }, {
    value: '现有的 StorageClass',
