export const rightToc = [{
  value: '集群中仅有 Linux 节点',
  id: '集群中仅有-linux-节点',
  children: []
}, {
  value: '集群中同时含有 Linux 节点 和 Windows 节点',
  id: '集群中同时含有-linux-节点-和-windows-节点',
  children: [{
    value: '同步 Windows 镜像',
    id: '同步-windows-镜像',
    children: []
