  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '4、安装 Rancher',
	description: '本节提供了较早版本的使用 Helm 2 离线安装 Rancher 的方法，如果无法升级到 Helm 3，可以使用此方法。本节介绍如何为离线安装 Rancher 准备您的节点。Rancher Server 可能会离线安装在防火墙或代理之后的封闭环境。这里有两个选项卡，用于高可用性（推荐）或 Docker 安装。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'安装指南',
		'资料、参考和高级选项',
		'Rancher 高可用 Helm2 离线安装',
		'安装 Rancher'
	]
};
export const metadata = {
  "id": "rancher2/installation/options/air-gap-helm2/install-rancher/_index",
  "title": "4、安装 Rancher",
