  "source": "@site/docs/rancher2/installation/options/air-gap-helm2/install-rancher/_index.md",
  "permalink": "/docs/rancher2/installation/options/air-gap-helm2/install-rancher/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/installation/options/air-gap-helm2/install-rancher/_index.md",
