  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "在25dashhboard启动监控grafana对接ldap"
    }}>{`在2.5dashhboard启动监控grafana对接ldap`}</h1>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`作者`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`王哲`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`团队`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Filed&&Suppoort`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`编写时间`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`2021/5/19`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`类型`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`监控功能使用问题`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "开启grafana-ldap认证"
    }}>{`开启grafana ldap认证`}</h2>
    <h4 {...{
      "id": "1、进入集群-应用商店-rancher-monitoring-编辑升级"
    }}>{`1、进入集群-应用商店-rancher-monitoring-编辑升级`}</h4>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E5%9C%A82.5dashhboard%E5%90%AF%E5%8A%A8%E7%9B%91%E6%8E%A7grafana%E5%AF%B9%E6%8E%A5ldap/grafana-ldap-2.jpg",
        "alt": "image"
      }}></img></p>
    <h4 {...{
      "id": "2、添加配置参数开启ldap"
    }}>{`2、添加配置参数开启ldap`}</h4>
    <p>{`以yaml文件编辑在grafana.grafana.ini层级下添加`}</p>
    <pre><code parentName="pre" {...{}}>{`    auth.ldap:
      allow_sign_up: true
      config_file: /etc/grafana/ldap.toml
`}</code></pre>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E5%9C%A82.5dashhboard%E5%90%AF%E5%8A%A8%E7%9B%91%E6%8E%A7grafana%E5%AF%B9%E6%8E%A5ldap/grafana-ldap-3.jpg",
        "alt": "image"
      }}></img></p>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E5%9C%A82.5dashhboard%E5%90%AF%E5%8A%A8%E7%9B%91%E6%8E%A7grafana%E5%AF%B9%E6%8E%A5ldap/grafana-ldap-1.jpg",
        "alt": "image"
      }}></img></p>
    <h2 {...{
      "id": "3、配置对接ldap认证"
    }}>{`3、配置对接ldap认证`}</h2>
    <p>{`继续编辑yaml,在grafana层级下添加以下内容：`}</p>
    <pre><code parentName="pre" {...{}}>{`  ldap:
    config: |
      [[servers]] 
      host = "172.16.51.62" 
      port = 389 
      use_ssl = false 
      start_tls = false 
      ssl_skip_verify = true 
      bind_dn = "cn=admin,dc=rancherldap,dc=com"
      bind_password = 'Rancher123' 
      search_filter = "(cn=%s)" 
      search_base_dns = ["ou=rancher,dc=rancherldap,dc=com"] 
      [servers.attributes] 
      name = "givenName" 
      surname = "sn" 
      username = "cn" 
      member_of = "memberOf" 
      email = "email"
    enabled: true
`}</code></pre>
    <p>{`ldap.toml配置文件实例：`}</p>
    <p>{`请根据实际情况进行修改，配置文件参数解释请点击链接`}<a parentName="p" {...{
        "href": "https://grafana.com/docs/grafana/latest/auth/ldap/"
      }}>{`官方文档`}</a></p>
    <pre><code parentName="pre" {...{}}>{`[[servers]]
# Ldap server host (specify multiple hosts space separated)
host = "127.0.0.1"
# Default port is 389 or 636 if use_ssl = true
port = 389
# Set to true if LDAP server should use an encrypted TLS connection (either with STARTTLS or LDAPS)
use_ssl = false
# If set to true, use LDAP with STARTTLS instead of LDAPS
start_tls = false
# set to true if you want to skip SSL cert validation
ssl_skip_verify = false
# set to the path to your root CA certificate or leave unset to use system defaults
# root_ca_cert = "/path/to/certificate.crt"
# Authentication against LDAP servers requiring client certificates
# client_cert = "/path/to/client.crt"
# client_key = "/path/to/client.key"

# Search user bind dn
bind_dn = "cn=admin,dc=grafana,dc=org"
# Search user bind password
# If the password contains # or ; you have to wrap it with triple quotes. Ex """#password;"""
bind_password = 'grafana'

# User search filter, for example "(cn=%s)" or "(sAMAccountName=%s)" or "(uid=%s)"
# Allow login from email or username, example "(|(sAMAccountName=%s)(userPrincipalName=%s))"
search_filter = "(cn=%s)"

# An array of base dns to search through
search_base_dns = ["dc=grafana,dc=org"]

# group_search_filter = "(&(objectClass=posixGroup)(memberUid=%s))"
# group_search_filter_user_attribute = "distinguishedName"
# group_search_base_dns = ["ou=groups,dc=grafana,dc=org"]

# Specify names of the LDAP attributes your LDAP uses
[servers.attributes]
member_of = "memberOf"
email =  "email"
`}</code></pre>
    <h4 {...{
      "id": "4、配置成功后点击升级"
    }}>{`4、配置成功后点击升级`}</h4>
    <h4 {...{
      "id": "5、等待升级成功后重新登陆grafana"
    }}>{`5、等待升级成功后重新登陆grafana`}</h4>
    <p>{`使用admin用户登录默认密码为prom-operator`}</p>
    <p>{`在server admin这里可以看到ldap已经开启，选择一个用户进行搜索测试`}</p>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E5%9C%A82.5dashhboard%E5%90%AF%E5%8A%A8%E7%9B%91%E6%8E%A7grafana%E5%AF%B9%E6%8E%A5ldap/grafana-ldap-4.jpg",
        "alt": "image"
      }}></img></p>
    <h4 {...{
      "id": "6、配置用户角色"
    }}>{`6、配置用户角色`}</h4>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E5%9C%A82.5dashhboard%E5%90%AF%E5%8A%A8%E7%9B%91%E6%8E%A7grafana%E5%AF%B9%E6%8E%A5ldap/grafana-ldap-5.jpg",
        "alt": "image"
      }}></img></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
