  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Prometheus 参数',
	description: '在配置 Prometheus集群监控或项目监控时，有以下的可配置项。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'集群管理员指南',
		'集群工具',
		'监控',
		'Prometheus 参数'
	]
};
export const metadata = {
  "id": "rancher2/monitoring-alerting/2.0-2.4/cluster-monitoring/prometheus/_index",
  "title": "Prometheus 参数",
  "description": "在配置 Prometheus集群监控或项目监控时，有以下的可配置项。",
  "source": "@site/docs/rancher2/monitoring-alerting/2.0-2.4/cluster-monitoring/prometheus/_index.md",
  "permalink": "/docs/rancher2/monitoring-alerting/2.0-2.4/cluster-monitoring/prometheus/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/monitoring-alerting/2.0-2.4/cluster-monitoring/prometheus/_index.md",
