  "id": "octopus/octopus-ui/_index",
  "title": "Octopus UI",
  "description": ":::note 说明",
  "source": "@site/docs/octopus/octopus-ui/_index.md",
  "permalink": "/docs/octopus/octopus-ui/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/octopus/octopus-ui/_index.md",
  "lastUpdatedBy": "Author",
