export const rightToc = [{
  value: '从 Helm 图表中安装 Octopus-UI',
  id: '从-helm-图表中安装-octopus-ui',
  children: []
}, {
  value: '从 YAML 文件安装 Octopus-UI',
  id: '从-yaml-文件安装-octopus-ui',
  children: []
}, {
  value: '登入验证',
  id: '登入验证',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`说明`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Octopus-UI 当前仅适用于 k3s 集群。`}</p></div></div>
    <h2 {...{
      "id": "从-helm-图表中安装-octopus-ui"
    }}>{`从 Helm 图表中安装 Octopus-UI`}</h2>
    <p>{`默认情况下，`}<inlineCode parentName="p">{`Octopus-UI`}</inlineCode>{`在 Octopus`}<a parentName="p" {...{
        "href": "../install/_index"
      }}>{`Helm 图表`}</a>{`安装时会自动部署，您始终可以使用以下命令将其打开或关闭：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-shell",
        "metastring": "script",
        "script": true
      }}>{`$ helm upgrade -n octopus-system --set octopus-ui.enabled=true octopus octopus/octopus
`}</code></pre>
    <h2 {...{
      "id": "从-yaml-文件安装-octopus-ui"
    }}>{`从 YAML 文件安装 Octopus-UI`}</h2>
    <p>{`通过`}<inlineCode parentName="p">{`all-in-one`}</inlineCode>{` YAML 文件来部署 `}<inlineCode parentName="p">{`Octopus-UI`}</inlineCode>{`：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-shell",
        "metastring": "script",
        "script": true
      }}>{`kubectl apply -f https://raw.githubusercontent.com/cnrancher/octopus-api-server/master/deploy/e2e/all_in_one.yaml
`}</code></pre>
    <p>{`通过检查其 pod 和服务状态来验证 `}<inlineCode parentName="p">{`Octopus-UI`}</inlineCode>{`的状态。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-shell",
        "metastring": "script",
        "script": true
      }}>{`kubectl get po -n kube-system -l app.kubernetes.io/name=octopus-ui

NAME                                      READY   STATUS      RESTARTS   AGE
rancher-octopus-api-server-5c845c998b-pj2gr          1/1     Running     1          20s

kubectl get svc -n kube-system -l app.kubernetes.io/name=octopus-ui
NAME              TYPE           CLUSTER-IP    EXTERNAL-IP                PORT(S)          AGE
rancher-octopus-api-server   LoadBalancer   10.43.98.95   172.16.1.89,192.168.0.90   8443:31520/TCP   22s
`}</code></pre>
    <p>{`默认情况下，`}<inlineCode parentName="p">{`Octopus-UI`}</inlineCode>{`使用服务发现类型为`}<inlineCode parentName="p">{`LoadBalancer`}</inlineCode>{`的`}<inlineCode parentName="p">{`8443`}</inlineCode>{`端口，您可以通过其`}<inlineCode parentName="p">{`EXTERNAL-IP:8443`}</inlineCode>{`来访问它：`}</p>
    <p><img parentName="p" {...{
        "src": "/img/octopus/edge-ui.jpg",
        "alt": "Octopus-UI"
      }}></img></p>
    <h2 {...{
      "id": "登入验证"
    }}>{`登入验证`}</h2>
    <p><inlineCode parentName="p">{`Octopus-UI`}</inlineCode>{`使用 k3s 生成的用户名和密码进行身份验证，您可以从生成的 k3s`}<a parentName="p" {...{
        "href": "/docs/k3s/cluster-access/_index"
      }}>{`[KUBECONFIG]`}</a>{`文件中找到它。`}</p>
    </MDXLayout>;
}

;
