    <p><strong parentName="p">{`Audit Script:`}</strong>{` check_files_permissions.sh`}</p>
    <pre><code parentName="pre" {...{}}>{`#!/usr/bin/env bash

# This script is used to ensure the file permissions are set to 644 or
# more restrictive for all files in a given directory or a wildcard
# selection of files
#
# inputs:
#   $1 = /full/path/to/directory or /path/to/fileswithpattern
#                                   ex: !(*key).pem
#
#   $2 (optional) = permission (ex: 600)
#
# outputs:
#   true/false

# Turn on "extended glob" for use of '!' in wildcard
shopt -s extglob

# Turn off history to avoid surprises when using '!'
set -H

USER_INPUT=$1

if [[ "\${USER_INPUT}" == "" ]]; then
  echo "false"
  exit
fi


if [[ -d \${USER_INPUT} ]]; then
  PATTERN="\${USER_INPUT}/*"
else
  PATTERN="\${USER_INPUT}"
fi

PERMISSION=""
if [[ "$2" != "" ]]; then
  PERMISSION=$2
fi

FILES_PERMISSIONS=$(stat -c %n\\ %a \${PATTERN})

while read -r fileInfo; do
  p=$(echo \${fileInfo} | cut -d' ' -f2)

  if [[ "\${PERMISSION}" != "" ]]; then
    if [[ "$p" != "\${PERMISSION}" ]]; then
      echo "false"
      exit
    fi
  else
    if [[ "$p" != "644" && "$p" != "640" && "$p" != "600" ]]; then
      echo "false"
      exit
    fi
  fi
done <<< "\${FILES_PERMISSIONS}"


echo "true"
exit
`}</code></pre>
