        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`--s3-endpoint`}</inlineCode>{` 值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`指定 s3 端点 url (默认值："s3.amazonaws.com")`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`--access-key`}</inlineCode>{` 值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`指定 s3 accessKey`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`--secret-key`}</inlineCode>{` 值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`指定 s3 secretKey`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`--bucket-name`}</inlineCode>{` 值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`指定 s3 桶名称`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`--folder`}</inlineCode>{` 值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`指定 s3 在桶中到文件夹 `}<em parentName="td">{`自 v2.3.0 起可用`}</em></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`--region`}</inlineCode>{` 值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`指定 s3 桶位置 (可选)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`*`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`--ssh-agent-auth`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><a parentName="td" {...{
              "href": "/docs/rke/config-options/_index"
            }}>{`使用 SSH_AUTH_SOCK 定义的 SSH 代理身份验证`}</a></td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`--ignore-docker-version`}</inlineCode></td>
