  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'RBAC',
	description: '本节介绍了使用 rancher-cis-benchmark App 所需的权限。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
