  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'AWS'
};
export const metadata = {
  "id": "rke/config-options/cloud-providers/aws/_index",
  "title": "AWS",
  "description": "## 概述",
  "source": "@site/docs/rke/config-options/cloud-providers/aws/_index.md",
  "permalink": "/docs/rke/config-options/cloud-providers/aws/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rke/config-options/cloud-providers/aws/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '概述',
  id: '概述',
  children: []
}, {
  value: '配置 IAM',
  id: '配置-iam',
  children: [{
    value: '配置 controlplane 节点的 IAM 策略',
    id: '配置-controlplane-节点的-iam-策略',
    children: []
  }, {
    value: '配置 etcd 节点和 worker 节点的 IAM 策略',
    id: '配置-etcd-节点和-worker-节点的-iam-策略',
    children: []
  }]
}, {
  value: '标记 AWS 资源',
  id: '标记-aws-资源',
  children: []
}, {
  value: '标记负载均衡器',
  id: '标记负载均衡器',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "概述"
    }}>{`概述`}</h2>
    <p>{`您只需要将名称设置为`}<inlineCode parentName="p">{`aws`}</inlineCode>{`，就可以启用 AWS。所有集群节点必须已经配置了 `}<a parentName="p" {...{
        "href": "#%E9%85%8D%E7%BD%AE-IAM"
      }}>{`适当的 IAM 角色`}</a>{`，并且您的 AWS 资源必须 `}<a parentName="p" {...{
        "href": "#%E6%A0%87%E8%AE%B0-AWS-%E8%B5%84%E6%BA%90"
      }}>{`标记 AWS 资源`}</a>{`。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`cloud_provider:
  name: aws
`}</code></pre>
    <h2 {...{
      "id": "配置-iam"
    }}>{`配置 IAM`}</h2>
    <p>{`在启用了 AWS 云提供商的集群中，节点必须至少拥有`}<inlineCode parentName="p">{`ec2:Describe*`}</inlineCode>{`动作。`}</p>
    <p>{`为了使用 Kubernetes 的弹性负载均衡器(ELB)和 EBS 卷，节点需要拥有具有适当权限的 IAM 角色。`}</p>
    <h3 {...{
      "id": "配置-controlplane-节点的-iam-策略"
    }}>{`配置 controlplane 节点的 IAM 策略`}</h3>
    <p>{`请参考以下代码示例，配置具有 `}<inlineCode parentName="p">{`controlplane`}</inlineCode>{`角色的节点的 IAM 策略：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "Version": "2012-10-17",
  "Statement": [
    {
      "Effect": "Allow",
      "Action": [
        "autoscaling:DescribeAutoScalingGroups",
        "autoscaling:DescribeLaunchConfigurations",
        "autoscaling:DescribeTags",
        "ec2:DescribeInstances",
        "ec2:DescribeRegions",
        "ec2:DescribeRouteTables",
        "ec2:DescribeSecurityGroups",
        "ec2:DescribeSubnets",
        "ec2:DescribeVolumes",
        "ec2:CreateSecurityGroup",
        "ec2:CreateTags",
        "ec2:CreateVolume",
        "ec2:ModifyInstanceAttribute",
        "ec2:ModifyVolume",
        "ec2:AttachVolume",
        "ec2:AuthorizeSecurityGroupIngress",
        "ec2:CreateRoute",
        "ec2:DeleteRoute",
        "ec2:DeleteSecurityGroup",
        "ec2:DeleteVolume",
        "ec2:DetachVolume",
        "ec2:RevokeSecurityGroupIngress",
        "ec2:DescribeVpcs",
        "elasticloadbalancing:AddTags",
        "elasticloadbalancing:AttachLoadBalancerToSubnets",
        "elasticloadbalancing:ApplySecurityGroupsToLoadBalancer",
        "elasticloadbalancing:CreateLoadBalancer",
        "elasticloadbalancing:CreateLoadBalancerPolicy",
        "elasticloadbalancing:CreateLoadBalancerListeners",
        "elasticloadbalancing:ConfigureHealthCheck",
        "elasticloadbalancing:DeleteLoadBalancer",
        "elasticloadbalancing:DeleteLoadBalancerListeners",
        "elasticloadbalancing:DescribeLoadBalancers",
        "elasticloadbalancing:DescribeLoadBalancerAttributes",
        "elasticloadbalancing:DetachLoadBalancerFromSubnets",
        "elasticloadbalancing:DeregisterInstancesFromLoadBalancer",
        "elasticloadbalancing:ModifyLoadBalancerAttributes",
        "elasticloadbalancing:RegisterInstancesWithLoadBalancer",
        "elasticloadbalancing:SetLoadBalancerPoliciesForBackendServer",
        "elasticloadbalancing:AddTags",
        "elasticloadbalancing:CreateListener",
        "elasticloadbalancing:CreateTargetGroup",
        "elasticloadbalancing:DeleteListener",
        "elasticloadbalancing:DeleteTargetGroup",
        "elasticloadbalancing:DescribeListeners",
        "elasticloadbalancing:DescribeLoadBalancerPolicies",
        "elasticloadbalancing:DescribeTargetGroups",
        "elasticloadbalancing:DescribeTargetHealth",
        "elasticloadbalancing:ModifyListener",
        "elasticloadbalancing:ModifyTargetGroup",
        "elasticloadbalancing:RegisterTargets",
        "elasticloadbalancing:SetLoadBalancerPoliciesOfListener",
        "iam:CreateServiceLinkedRole",
        "kms:DescribeKey"
      ],
      "Resource": ["*"]
    }
  ]
}
`}</code></pre>
    <h3 {...{
      "id": "配置-etcd-节点和-worker-节点的-iam-策略"
    }}>{`配置 etcd 节点和 worker 节点的 IAM 策略`}</h3>
    <p>{`请参考以下代码示例，配置具有`}<inlineCode parentName="p">{`etcd`}</inlineCode>{` or `}<inlineCode parentName="p">{`worker`}</inlineCode>{`角色的节点的 IAM 策略：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "Version": "2012-10-17",
  "Statement": [
    {
      "Effect": "Allow",
      "Action": [
        "ec2:DescribeInstances",
        "ec2:DescribeRegions",
        "ecr:GetAuthorizationToken",
        "ecr:BatchCheckLayerAvailability",
        "ecr:GetDownloadUrlForLayer",
        "ecr:GetRepositoryPolicy",
        "ecr:DescribeRepositories",
        "ecr:ListImages",
        "ecr:BatchGetImage"
      ],
      "Resource": "*"
    }
  ]
}
`}</code></pre>
    <h2 {...{
      "id": "标记-aws-资源"
    }}>{`标记 AWS 资源`}</h2>
    <p>{`AWS 云提供商使用标记来发现和管理资源，以下资源不会被 Kubernetes 或 RKE 自动标记，需要用户手动添加标记。`}</p>
    <ul>
      <li parentName="ul"><strong parentName="li">{`VPC`}</strong>{`：集群使用的 VPC`}</li>
      <li parentName="ul"><strong parentName="li">{`子网`}</strong>{`：集群使用的子网`}</li>
      <li parentName="ul"><strong parentName="li">{`EC2 实例`}</strong>{`：为该集群启动的所有节点`}</li>
      <li parentName="ul"><strong parentName="li">{`安全组`}</strong>{`：集群中各节点使用的安全组`}</li>
    </ul>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{`如果创建`}<inlineCode parentName="p">{`LoadBalancer`}</inlineCode>{`服务，且节点上连接有多个安全组，则必须仅将其中一个安全组标记为`}<inlineCode parentName="p">{`owned`}</inlineCode>{`，以便 Kubernetes 知道要添加和删除哪个组的规则。允许使用一个未标记的安全组，但是，不建议在集群之间共享这个安全组。`}</p>
    </blockquote>
    <p><a parentName="p" {...{
        "href": "https://docs.aws.amazon.com/AWSEC2/latest/UserGuide/Using_Tags.html"
      }}>{`标记 Amazon EC2 资源`}</a></p>
    <p><inlineCode parentName="p">{`<CLUSTERID>`}</inlineCode>{`可以是你选择的任何字符串。但是，必须在标记的每个资源上使用相同的字符串。将标签值设置为`}<inlineCode parentName="p">{`owned`}</inlineCode>{`会通知集群，所有用`}<inlineCode parentName="p">{`<CLUSTERID>`}</inlineCode>{`标记的资源都只由这个集群拥有和管理。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Key`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Value`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kubernetes.io/cluster/`}<inlineCode parentName="td">{`<CLUSTERID>`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`shared`}</td>
        </tr>
      </tbody>
    </table>
    <p>{`如果您不在集群之间共享资源，您可以将标签改为：`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Key`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Value`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kubernetes.io/cluster/`}<inlineCode parentName="td">{`<CLUSTERID>`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`owned`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "标记负载均衡器"
    }}>{`标记负载均衡器`}</h2>
    <p>{`在配置`}<inlineCode parentName="p">{`LoadBalancer`}</inlineCode>{`服务时，Kubernetes 会尝试发现正确的子网，这也是通过标签来实现的，需要添加额外的子网标签，以确保在正确的子网中创建面向互联网和内部的 ELB。`}</p>
    <p><a parentName="p" {...{
        "href": "https://docs.aws.amazon.com/zh_cn/eks/latest/userguide/load-balancing.html#subnet-tagging-for-load-balancers"
      }}>{`AWS 负载均衡器的子网标记`}</a></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
