  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '安装选项介绍',
	description: '本页主要介绍首次设置K3s时可以使用的选项',
	keywords: [
		'k3s中文文档',
		'k3s 中文文档',
		'k3s中文',
		'k3s 中文',
		'k3s',
		'k3s教程',
		'k3s中国',
		'rancher',
		'k3s 中文教程',
		'安装介绍',
		'安装选项介绍'
	]
};
export const metadata = {
  "id": "k3s/installation/install-options/_index",
  "title": "安装选项介绍",
  "description": "本页主要介绍首次设置K3s时可以使用的选项",
  "source": "@site/docs/k3s/installation/install-options/_index.md",
  "permalink": "/docs/k3s/installation/install-options/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/k3s/installation/install-options/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '使用脚本安装的选项',
  id: '使用脚本安装的选项',
  children: []
}, {
  value: '从二进制中安装 K3s',
  id: '从二进制中安装-k3s',
  children: [{
    value: 'K3s Server 的注册选项',
    id: 'k3s-server-的注册选项',
    children: []
  }, {
    value: 'K3s Agent 的注册选项',
    id: 'k3s-agent-的注册选项',
    children: []
  }, {
    value: '配置文件',
    id: '配置文件',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`本页主要介绍首次设置 K3s 时可以使用的选项：`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#%E4%BD%BF%E7%94%A8%E8%84%9A%E6%9C%AC%E5%AE%89%E8%A3%85%E7%9A%84%E9%80%89%E9%A1%B9"
        }}>{`使用脚本安装的选项`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#%E4%BB%8E%E4%BA%8C%E8%BF%9B%E5%88%B6%E4%B8%AD%E5%AE%89%E8%A3%85k3s"
        }}>{`从二进制中安装的选项`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#k3s-server%E7%9A%84%E6%B3%A8%E5%86%8C%E9%80%89%E9%A1%B9"
        }}>{`K3s server 的注册选项`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#k3s-agent%E7%9A%84%E6%B3%A8%E5%86%8C%E9%80%89%E9%A1%B9"
        }}>{`K3s agent 的注册选项`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#%E9%85%8D%E7%BD%AE%E6%96%87%E4%BB%B6"
        }}>{`配置文件`}</a></li>
    </ul>
    <p>{`除了使用环境变量和 CLI 参数配置 K3s 外，K3s 还可以使用`}<a parentName="p" {...{
        "href": "#%E9%85%8D%E7%BD%AE%E6%96%87%E4%BB%B6"
      }}>{`配置文件`}</a></p>
    <p>{`更多高级选项，请参阅`}<a parentName="p" {...{
        "href": "/docs/k3s/advanced/_index"
      }}>{`本页`}</a>{`。`}</p>
    <blockquote>
      <p parentName="blockquote">{`在整个 K3s 文档中，你会看到一些选项可以作为命令标志和环境变量传递进来。关于传入选项的帮助，请参考`}<a parentName="p" {...{
          "href": "/docs/k3s/installation/install-options/how-to-flags/_index"
        }}>{`如何使用标志和环境变量`}</a>{`。`}</p>
    </blockquote>
    <h2 {...{
      "id": "使用脚本安装的选项"
    }}>{`使用脚本安装的选项`}</h2>
    <p>{`正如`}<a parentName="p" {...{
        "href": "/docs/k3s/quick-start/_index"
      }}>{`快速启动指南`}</a>{`中提到的那样，你可以使用`}<a parentName="p" {...{
        "href": "https://get.k3s.io"
      }}>{`https://get.k3s.io`}</a>{` 提供的安装脚本在基于 systemd 和 openrc 的系统上安装 K3s 作为服务。`}</p>
    <p>{`该命令的最简单形式如下：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-sh"
      }}>{`curl -sfL https://get.k3s.io | sh -
`}</code></pre>
    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`提示`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`国内用户，可以使用以下方法加速安装：`}</p><pre parentName="div"><code parentName="pre" {...{}}>{`curl -sfL http://rancher-mirror.cnrancher.com/k3s/k3s-install.sh | INSTALL_K3S_MIRROR=cn sh -
`}</code></pre></div></div>
    <p>{`使用此方法安装 K3s 时，可使用以下环境变量来配置安装:`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Environment Variable`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_SKIP_DOWNLOAD`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果设置为 "true "将不会下载 K3s 的哈希值或二进制。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_SYMLINK`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`默认情况下，如果路径中不存在命令，将为 kubectl、crictl 和 ctr 二进制文件创建符号链接。如果设置为'skip'将不会创建符号链接，而'force'将覆盖。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_SKIP_ENABLE`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果设置为 "true"，将不启用或启动 K3s 服务。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_SKIP_START`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果设置为 "true "将不会启动 K3s 服务。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_VERSION`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`从 Github 下载 K3s 的版本。如果没有指定，将尝试从"stable"频道下载。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_BIN_DIR`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`安装 K3s 二进制文件、链接和卸载脚本的目录，或者使用`}<inlineCode parentName="td">{`/usr/local/bin`}</inlineCode>{`作为默认目录。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_BIN_DIR_READ_ONLY`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果设置为 true 将不会把文件写入`}<inlineCode parentName="td">{`INSTALL_K3S_BIN_DIR`}</inlineCode>{`，强制设置`}<inlineCode parentName="td">{`INSTALL_K3S_SKIP_DOWNLOAD=true`}</inlineCode>{`。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_SYSTEMD_DIR`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`安装 systemd 服务和环境文件的目录，或者使用`}<inlineCode parentName="td">{`/etc/systemd/system`}</inlineCode>{`作为默认目录。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_EXEC`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`带有标志的命令，用于在服务中启动 K3s。如果未指定命令，并且设置了`}<inlineCode parentName="td">{`K3S_URL`}</inlineCode>{`，它将默认为“agent”。如果未设置`}<inlineCode parentName="td">{`K3S_URL`}</inlineCode>{`，它将默认为“server”。要获得帮助，请参考`}<a parentName="td" {...{
              "href": "/docs/k3s/installation/install-options/how-to-flags/_index#%E7%A4%BA%E4%BE%8B-b-install_k3s_exec"
            }}>{`此示例。`}</a></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_NAME`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`要创建的 systemd 服务名称，如果以服务器方式运行 k3s，则默认为'k3s'；如果以 agent 方式运行 k3s，则默认为'k3s-agent'。如果指定了服务名，则服务名将以'k3s-'为前缀。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_TYPE`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`要创建的 systemd 服务类型，如果没有指定，将默认使用 K3s exec 命令。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_SELINUX_WARN`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果设置为 true，则在没有找到 k3s-selinux 策略的情况下将继续。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_SKIP_SELINUX_RPM`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果设置为 "true "将跳过 k3s RPM 的自动安装。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_CHANNEL_URL`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`用于获取 K3s 下载网址的频道 URL。默认为 `}<a parentName="td" {...{
              "href": "https://update.k3s.io/v1-release/channels"
            }}>{`https://update.k3s.io/v1-release/channels`}</a>{` 。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`INSTALL_K3S_CHANNEL`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`用于获取 K3s 下载 URL 的通道。默认值为 "stable"。选项包括：`}<inlineCode parentName="td">{`stable`}</inlineCode>{`, `}<inlineCode parentName="td">{`latest`}</inlineCode>{`, `}<inlineCode parentName="td">{`testing`}</inlineCode>{`。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`K3S_CONFIG_FILE`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`指定配置文件的位置。默认目录为`}<inlineCode parentName="td">{`/etc/rancher/k3s/config.yaml`}</inlineCode>{`。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`K3S_TOKEN`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`用于将 server 或 agent 加入集群的共享 secret。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`K3S_TOKEN_FILE`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`指定 `}<inlineCode parentName="td">{`cluster-secret`}</inlineCode>{`,`}<inlineCode parentName="td">{`token`}</inlineCode>{` 的文件目录。`}</td>
        </tr>
      </tbody>
    </table>
    <p>{`以 "K3S`}{`_`}{`"开头的环境变量将被保留，供 systemd 和 openrc 服务使用。`}</p>
    <p>{`在没有明确设置 exec 命令的情况下设置`}<inlineCode parentName="p">{`K3S_URL`}</inlineCode>{`，会将命令默认为 "agent"。`}</p>
    <p>{`运行 agent 时还必须设置`}<inlineCode parentName="p">{`K3S_TOKEN`}</inlineCode>{`。`}</p>
    <h2 {...{
      "id": "从二进制中安装-k3s"
    }}>{`从二进制中安装 K3s`}</h2>
    <p>{`如上所述，安装脚本主要是配置 K3s 作为服务运行。如果你选择不使用脚本，你可以通过从我们的`}<a parentName="p" {...{
        "href": "https://github.com/rancher/k3s/releases/latest"
      }}>{`发布页面`}</a>{`下载二进制文件，将其放在你的路径上，然后执行它来运行 K3s。K3s 二进制支持以下命令：`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`命令`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`k3s server`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`运行 K3s server，它还将启动 Kubernetes control-plane 组件，如 API server, controller-manager, 和 scheduler。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`k3s agent`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`运行 K3s agent 节点。这将使 K3s 作为工作节点运行，启动 Kubernetes 节点服务`}<inlineCode parentName="td">{`kubelet`}</inlineCode>{`和`}<inlineCode parentName="td">{`kube-proxy`}</inlineCode>{`。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`k3s kubectl`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`运行嵌入式`}<a parentName="td" {...{
              "href": "https://kubernetes.io/docs/reference/kubectl/overview/"
            }}>{`kubectl`}</a>{` CLI。如果没有设置`}<inlineCode parentName="td">{`KUBECONFIG`}</inlineCode>{`环境变量，当启动 K3s 服务器节点时，将自动尝试使用在`}<inlineCode parentName="td">{`/etc/rancher/k3s/k3s.yaml`}</inlineCode>{`创建的配置文件。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`k3s crictl`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`运行一个嵌入式`}<a parentName="td" {...{
              "href": "https://github.com/kubernetes-sigs/cri-tools/blob/master/docs/crictl.md"
            }}>{`crictl`}</a>{`。这是一个用于与 Kubernetes 的容器运行时接口（CRI）交互的 CLI。对调试很有用。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`k3s ctr`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`运行一个嵌入式的`}<a parentName="td" {...{
              "href": "https://github.com/projectatomic/containerd/blob/master/docs/cli.md"
            }}>{`ctr`}</a>{`。这是为 containerd（K3s 使用的容器守护进程）提供的 CLI。对调试很有用。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`k3s help`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`显示一个命令的命令列表或帮助。`}</td>
        </tr>
      </tbody>
    </table>
    <p><inlineCode parentName="p">{`k3s server`}</inlineCode>{` 和 `}<inlineCode parentName="p">{`k3s agent`}</inlineCode>{` 命令有额外的配置选项，可以通过 `}<inlineCode parentName="p">{`k3s server --help`}</inlineCode>{` 或 `}<inlineCode parentName="p">{`k3s agent --help`}</inlineCode>{` 查看.`}</p>
    <h3 {...{
      "id": "k3s-server-的注册选项"
    }}>{`K3s Server 的注册选项`}</h3>
    <p>{`关于 K3s server 的详细配置，请参考`}<a parentName="p" {...{
        "href": "/docs/k3s/installation/install-options/server-config/_index"
      }}>{`k3s server 配置参考`}</a></p>
    <h3 {...{
      "id": "k3s-agent-的注册选项"
    }}>{`K3s Agent 的注册选项`}</h3>
    <p>{`关于 K3s agent 的配置详情，请参考`}<a parentName="p" {...{
        "href": "/docs/k3s/installation/install-options/agent-config/_index"
      }}>{`k3s agent 配置参考`}</a></p>
    <h3 {...{
      "id": "配置文件"
    }}>{`配置文件`}</h3>
    <p><em parentName="p">{`Available as of v1.19.1+k3s1`}</em></p>
    <p>{`除了使用环境变量和 CLI 参数来配置 K3s，K3s 还可以使用配置文件。`}</p>
    <p>{`默认情况下，位于`}<inlineCode parentName="p">{`/etc/rancher/k3s/config.yaml`}</inlineCode>{`的 YAML 文件中的值将在安装时使用。`}</p>
    <p>{`下面是一个基本的`}<inlineCode parentName="p">{`server`}</inlineCode>{`配置文件的例子。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`write-kubeconfig-mode: "0644"
tls-san:
  - "foo.local"
node-label:
  - "foo=bar"
  - "something=amazing"
`}</code></pre>
    <p>{`一般来说，CLI 参数映射到各自的 YAML 键，可重复的 CLI 参数被表示为 YAML 列表。`}</p>
    <p>{`下面展示了一个完全使用 CLI 参数的相同配置来证明这一点。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`k3s server \\
  --write-kubeconfig-mode "0644"    \\
  --tls-san "foo.local"             \\
  --node-label "foo=bar"            \\
