  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Rancher Agent',
	description: 'Rancher 纳管的集群部署有两种不同的Agent ：cattle-cluster-agent 和 cattle-node-agent',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'创建集群',
		'集群配置参数',
		'配置 Pod 安全策略'
	]
};
export const metadata = {
  "id": "rancher2/cluster-provisioning/rke-clusters/rancher-agents/_index",
  "title": "Rancher Agent",
  "description": "Rancher 纳管的集群部署有两种不同的Agent ：cattle-cluster-agent 和 cattle-node-agent",
  "source": "@site/docs/rancher2/cluster-provisioning/rke-clusters/rancher-agents/_index.md",
  "permalink": "/docs/rancher2/cluster-provisioning/rke-clusters/rancher-agents/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/cluster-provisioning/rke-clusters/rancher-agents/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'cattle-cluster-agent',
  id: 'cattle-cluster-agent',
  children: []
}, {
  value: 'cattle-node-agent',
  id: 'cattle-node-agent',
  children: []
}, {
  value: '调度规则',
  id: '调度规则',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Rancher 纳管的集群部署有两种不同的 Agent：`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#cattle-cluster-agent"
        }}>{`cattle-cluster-agent`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#cattle-node-agent"
        }}>{`cattle-node-agent`}</a></li>
    </ul>
    <p>{`有关 Rancher Server 如何设置集群并与之通信的概述，请参阅`}<a parentName="p" {...{
        "href": "/docs/rancher2/overview/architecture/_index"
      }}>{`产品架构`}</a>{`。`}</p>
    <h2 {...{
      "id": "cattle-cluster-agent"
    }}>{`cattle-cluster-agent`}</h2>
    <p><inlineCode parentName="p">{`cattle-cluster-agent`}</inlineCode>{`用于连接集群的`}<a parentName="p" {...{
        "href": "/docs/rancher2/cluster-provisioning/rke-clusters/_index"
      }}>{`Rancher 部署的 Kubernetes 集群`}</a>{`的 Kubernetes API。`}<inlineCode parentName="p">{`cattle-cluster-agent`}</inlineCode>{`通过 Deployment 的方式部署。`}</p>
    <h2 {...{
      "id": "cattle-node-agent"
    }}>{`cattle-node-agent`}</h2>
    <p>{`在执行集群操作时，`}<inlineCode parentName="p">{`cattle-node-agent`}</inlineCode>{`用于和`}<a parentName="p" {...{
        "href": "/docs/rancher2/cluster-provisioning/rke-clusters/_index"
      }}>{`Rancher 部署的 Kubernetes 集群`}</a>{`中的节点进行交互。集群操作的示例包括升级 Kubernetes 版本、创建 etcd 快照和恢复 etcd 快照。`}<inlineCode parentName="p">{`cattle-node-agent`}</inlineCode>{`通过 DaemonSet 的方式部署，以确保其在每个节点上运行。当`}<inlineCode parentName="p">{`cattle-cluster-agent`}</inlineCode>{`不可用时，`}<inlineCode parentName="p">{`cattle-node-agent`}</inlineCode>{` 将作为备选方案连接到`}<a parentName="p" {...{
        "href": "/docs/rancher2/cluster-provisioning/rke-clusters/_index"
      }}>{`Rancher 部署的 Kubernetes 集群`}</a>{`中的 Kubernetes API。`}</p>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{` 在 Rancher v2.2.4 及以下版本中，`}<inlineCode parentName="p">{`cattle-node-agent`}</inlineCode>{` pod 没有忍受所有的 taints，导致 Kubernetes 升级失败。Rancher v2.2.5 及更高版本中包含了对此问题的修复。`}</p>
    </blockquote>
    <h2 {...{
      "id": "调度规则"
    }}>{`调度规则`}</h2>
    <p><em parentName="p">{`适用于 v2.3.0 及更高版本`}</em></p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`组件`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`节点亲和性和节点选择器`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`节点选择器`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`容忍`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`cattle-cluster-agent`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`beta.kubernetes.io/os:NotIn:windows`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`none`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`operator:Exists`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`cattle-node-agent`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`beta.kubernetes.io/os:NotIn:windows`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`none`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`operator:Exists`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    <p>{`因为`}<inlineCode parentName="p">{`cattle-cluster-agent`}</inlineCode>{` 部署配置了`}<inlineCode parentName="p">{`requiredDuringSchedulingIgnoredDuringExecution`}</inlineCode>{`相关的首选调度规则设置，所以它会在 `}<inlineCode parentName="p">{`controlplane`}</inlineCode>{` 角色的节点上执行调度。参考`}<a parentName="p" {...{
        "href": "https://kubernetes.io/docs/concepts/configuration/assign-pod-node/"
      }}>{`Kubernetes: 将 pod 分配给节点`}</a>{`，可以找到更多关于调度规则的信息。`}</p>
    <p><inlineCode parentName="p">{`requiredDuringSchedulingIgnoredDuringExecution`}</inlineCode>{` 配置如下表所示:`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`权重`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`表达式`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`100`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`node-role.kubernetes.io/controlplane:In:"true"`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`node-role.kubernetes.io/etcd:In:"true"`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
