  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'DeviceLink 的状态',
	description: '在 `DeviceLink`的状态中，有下列几种条件可用于跟踪其链接的状态，NodeExisted：节点是否可用；ModelExisted：设备模型是否存在；AdaptorExisted：适配器是否可用；DeviceCreated：是否已创建设备；DeviceConnected：是否已连接设备。',
	keywords: [
		'Octopus中文文档',
		'Octopus 中文文档',
		'边缘计算',
		'IOT',
		'edge computing',
		'Octopus中文',
		'Octopus 中文',
		'Octopus',
		'Octopus教程',
		'Octopus中国',
		'rancher',
		'Octopus 中文教程',
		'DeviceLink',
		'DeviceLink 的状态'
	]
};
export const metadata = {
  "id": "octopus/device-link/state-of-dl/_index",
  "title": "DeviceLink 的状态",
  "description": "在 `DeviceLink`的状态中，有下列几种条件可用于跟踪其链接的状态，NodeExisted：节点是否可用；ModelExisted：设备模型是否存在；AdaptorExisted：适配器是否可用；DeviceCreated：是否已创建设备；DeviceConnected：是否已连接设备。",
  "source": "@site/docs/octopus/device-link/state-of-dl/_index.md",
  "permalink": "/docs/octopus/device-link/state-of-dl/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/octopus/device-link/state-of-dl/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '主要流程',
  id: '主要流程',
  children: []
}, {
  value: '流程与行为',
  id: '流程与行为',
  children: []
}, {
  value: '设备连接状态',
  id: '设备连接状态',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "主要流程"
    }}>{`主要流程`}</h2>
    <p>{`在 `}<inlineCode parentName="p">{`DeviceLink`}</inlineCode>{`的状态中，有下列几种条件可用于跟踪其链接的状态：`}</p>
    <ul>
      <li parentName="ul">{`NodeExisted：节点是否可用`}</li>
      <li parentName="ul">{`ModelExisted：设备模型是否存在`}</li>
      <li parentName="ul">{`AdaptorExisted：适配器是否可用`}</li>
      <li parentName="ul">{`DeviceCreated：是否已创建设备`}</li>
      <li parentName="ul">{`DeviceConnected：是否已连接设备`}</li>
    </ul>
    <p>{`这是 `}<inlineCode parentName="p">{`DeviceLink`}</inlineCode>{`的示例：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`apiVersion: edge.cattle.io/v1alpha1
kind: DeviceLink
metadata:
  name: example
  namespace: octopus-test
spec:
  adaptor:
    node: edge-worker
    name: adaptors.edge.cattle.io/dummy
  model:
    apiVersion: "devices.edge.cattle.io/v1alpha1"
    kind: "DummyDevice"
  template:
    metadata:
      labels:
        device: example
    spec:
      gear: slow
      "on": true
`}</code></pre>
    <p>{`当我们将上述`}<inlineCode parentName="p">{`DeviceLink`}</inlineCode>{`部署到集群中时，`}<inlineCode parentName="p">{`brain`}</inlineCode>{`负载校验`}<inlineCode parentName="p">{`NodeExisted`}</inlineCode>{`和`}<inlineCode parentName="p">{`ModelExisted`}</inlineCode>{`，`}<inlineCode parentName="p">{`limb`}</inlineCode>{`负载校验`}<inlineCode parentName="p">{`AdaptorExisted`}</inlineCode>{`、`}<inlineCode parentName="p">{`DeviceCreated`}</inlineCode>{`和`}<inlineCode parentName="p">{`DeviceConnected`}</inlineCode>{`：`}</p>
    <p>{`首先，`}<inlineCode parentName="p">{`brain`}</inlineCode>{`会验证`}<inlineCode parentName="p">{`spec.adaptor.node`}</inlineCode>{`中指定的节点是否可用：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`┌─────────────────────┐   节点是否可用？
│     NodeExisted     │───────────────┐
└─────────────────────┘               │
                                      ▼
                                      .
                                     ( ) brain
                                      '
`}</code></pre>
    <p>{`如果该节点可用，`}<inlineCode parentName="p">{`brain`}</inlineCode>{`会验证 CRD 对应的`}<inlineCode parentName="p">{`spec.model`}</inlineCode>{`(设备模型)是否存在：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`┌─────────────────────┐   节点是否可用？
│     NodeExisted     │───────────────┐
└─────────────────────┘               │
                                      ▼
                          节点可用         .
           ┌─────────────────────────( ) brain
           │                          '
           ▼
┌─────────────────────┐   设备模型是否存在？
│    ModelExisted     │───────────────┐
└─────────────────────┘               │
                                      ▼
                                      .
                                     ( ) brain
                                      '
`}</code></pre>
    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`说明`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`用作设备模型的 CRD 需要包含注释：`}<inlineCode parentName="p">{`devices.edge.cattle.io/enable：true`}</inlineCode>{`。`}</p></div></div>
    <p>{`如果 CRD（设备模型）验证通过，则`}<inlineCode parentName="p">{`limb`}</inlineCode>{`将验证适配器（Adaptor）对应的`}<inlineCode parentName="p">{`spec.adaptor.name`}</inlineCode>{`是否可用：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`           │
           ▼
┌─────────────────────┐   设备模型是否存在？
│    ModelExisted     │───────────────┐
└─────────────────────┘               │
                                      ▼
                          设备模型存在        .
           ┌─────────────────────────( ) brain
           │                          '
           ▼
┌─────────────────────┐   适配器（Adaptor）是否可用？
│   AdaptorExisted    │───────────────┐
└─────────────────────┘               │
                                      ▼
                                      .
                                     ( ) limb
                                      '
`}</code></pre>
    <p>{`您可以检查 Adaptor 的设计来了解`}<inlineCode parentName="p">{`limb`}</inlineCode>{`如何检测适配器。 如果适配器可用，那么`}<inlineCode parentName="p">{`limb`}</inlineCode>{`将尝试创建与`}<inlineCode parentName="p">{`spec.model`}</inlineCode>{`相关的设备实例：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`           │
           ▼
┌─────────────────────┐   适配器（Adaptor）是否可用？
│   AdaptorExisted    │───────────────┐
└─────────────────────┘               │
                                      ▼
                          yes         .
           ┌─────────────────────────( ) limb
           │                          '
           ▼
┌─────────────────────┐   基于设备模型创建设备实例
│    DeviceCreated    │───────────────┐
└─────────────────────┘               │
                                      ▼
                                      .
                                     ( ) limb
                                      '
`}</code></pre>
    <p>{`成功创建设备实例后，`}<inlineCode parentName="p">{`limb`}</inlineCode>{`将使用`}<inlineCode parentName="p">{`spec.template.spec`}</inlineCode>{`通过适配器（Adaptor）连接该实际设备：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`           │
           ▼
┌─────────────────────┐   创建设备实例
│    DeviceCreated    │───────────────┐
└─────────────────────┘               │
                                      ▼
                          创建成功     .
           ┌─────────────────────────( ) limb
           │                          '
           ▼
┌─────────────────────┐   连接到真实设备
│   DeviceConnected   │───────────────┐
└─────────────────────┘               │
                                      ▼
                                      .
                                     ( ) limb
                                      '
`}</code></pre>
    <p>{`如果连接状态是`}<inlineCode parentName="p">{`healthy`}</inlineCode>{`，则相应的设备实例将从实际物理设备同步其状态。 以下是所有状态的完整过程：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`┌─────────────────────┐   节点是否可用？
│     NodeExisted     │───────────────┐
└─────────────────────┘               │
                                      ▼
                          节点可用         .
           ┌─────────────────────────( ) brain
           │                          '
           ▼
┌─────────────────────┐   设备模型是否存在？
│    ModelExisted     │───────────────┐
└─────────────────────┘               │
                                      ▼
                          存在设备模型        .
           ┌─────────────────────────( ) brain
           │                          '
           ▼
┌─────────────────────┐   适配器（Adaptor）是否可用?
│   AdaptorExisted    │───────────────┐
└─────────────────────┘               │
                                      ▼
                          适配器可用         .
           ┌─────────────────────────( ) limb
           │                          '
           ▼
┌─────────────────────┐   创建设备实例
│    DeviceCreated    │───────────────┐
└─────────────────────┘               │
                                      ▼
                          创建成功     .
           ┌─────────────────────────( ) limb
           │                          '
           ▼
┌─────────────────────┐  连接到真实设备
│   DeviceConnected   │───────────────┐
└─────────────────────┘               │
           ▲                          ▼
           │              healthy     .
           └─────────────────────────( ) limb ─────────┐
                                      '                │
                                      ▲                ▼
                                      │                .
                                      └───────────────( ) adaptor
                                                       '
`}</code></pre>
    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`说明`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`DeviceLink 的状态流是有序执行的，如果前一个状态未准备就绪（不成功或错误），则不会进入到下一个状态。`}</p></div></div>
    <h2 {...{
      "id": "流程与行为"
    }}>{`流程与行为`}</h2>
    <p>{`主要流程并不一定总是向前发展，某些检测逻辑可以对其进行调整以显示当前状态，我们称它们为`}<inlineCode parentName="p">{`更正`}</inlineCode>{`。 有些更正是自动的，但有些需要手动干预。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`状态`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`控制器`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`逻辑描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`NodeExisted`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`brain`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果该节点已被删除、驱逐或束缚，则 Octopus 的`}<inlineCode parentName="td">{`brain`}</inlineCode>{`会将主流重新调整为`}<inlineCode parentName="td">{`NodeExisted`}</inlineCode>{`，并将其标记为不可用。 当节点再次可用时，`}<inlineCode parentName="td">{`brain`}</inlineCode>{`将触发主流重新开始。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`ModelExisted`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`brain`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果 CRD（设备模型）已被删除或禁用，则`}<inlineCode parentName="td">{`brain`}</inlineCode>{`会将主流调整回`}<inlineCode parentName="td">{`ModelExisted`}</inlineCode>{`并标记为不可用。当 CRD 再次可用时，`}<inlineCode parentName="td">{`brain`}</inlineCode>{`将触发主要流程，从模型检测开始。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`AdaptorExisted`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`limb`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`如果已删除适配器，则`}<inlineCode parentName="td">{`limbs`}</inlineCode>{`会将主流调整回`}<inlineCode parentName="td">{`AdaptorExisted`}</inlineCode>{`并标记为不可用。当适配器再次可用时，`}<inlineCode parentName="td">{`limb`}</inlineCode>{`将触发主要流程，从适配器检测开始。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`DeviceConnected`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`limb`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`limbs`}</inlineCode>{`不会立即察觉到设备实例的意外删除，因为`}<inlineCode parentName="td">{`limbs`}</inlineCode>{`通过适配器控制设备，无法监视到这些真实设备的实例。如果删除处于已经连接状态（`}<inlineCode parentName="td">{`DeviceConnected: healthy`}</inlineCode>{`）的设备，并且适配器的实现是实时或定时地同步实际设备的状态，则它可能有机会由`}<inlineCode parentName="td">{`limb`}</inlineCode>{`重新创建。 `}<inlineCode parentName="td">{`limb`}</inlineCode>{`将触发主要流程再次从设备创建开始。`}<strong parentName="td">{`否则，该链接需要手动修改或重新创建该设备。`}</strong></td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "设备连接状态"
    }}>{`设备连接状态`}</h2>
    <p>{`在谈论`}<inlineCode parentName="p">{`DeviceConnected`}</inlineCode>{`之前，需要知道 Octopus 的设备连接管理分为两部分，一个是`}<inlineCode parentName="p">{`limb`}</inlineCode>{`与适配器之间的连接（c1），另一个是适配器与真实设备之间的连接（c2），如下图所示：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`┌──────────┐   c1   ┌─────────┐   c2    .
│   limb   │◀──────▶│ adaptor │◀──────▶( ) real device
└──────────┘        └─────────┘         '
`}</code></pre>
    <p>{`c1 基于`}<a parentName="p" {...{
        "href": "https://grpc.io/"
      }}>{`gRPC`}</a>{`，而 c2 由适配器确定。 当 c1 和 c2 都健康时，`}<inlineCode parentName="p">{`limb`}</inlineCode>{`会将 DeviceConnected 状态设置为`}<inlineCode parentName="p">{`healthy`}</inlineCode>{`。`}</p>
    <p><inlineCode parentName="p">{`limb`}</inlineCode>{`可以观察到`}<inlineCode parentName="p">{`c1`}</inlineCode>{`的变化，如果`}<inlineCode parentName="p">{`c1`}</inlineCode>{`意外关闭，则`}<inlineCode parentName="p">{`limb`}</inlineCode>{`会触发主流从设备连接重新开始。`}</p>
    <p>{`但是，`}<inlineCode parentName="p">{`limb`}</inlineCode>{` 无法感知 `}<inlineCode parentName="p">{`c2`}</inlineCode>{`是否意外关闭，因此适配器需要负责通知其状态，通常，适配器需要向`}<inlineCode parentName="p">{`limb`}</inlineCode>{`发送`}<inlineCode parentName="p">{`ERROR`}</inlineCode>{`状态的消息。 然后，`}<inlineCode parentName="p">{`limb`}</inlineCode>{`会将 `}<inlineCode parentName="p">{`DeviceConnected`}</inlineCode>{`状态设置为`}<inlineCode parentName="p">{`unhealthy`}</inlineCode>{`。`}</p>
    <p>{`如果中断的`}<inlineCode parentName="p">{`c2`}</inlineCode>{`没有重新连接，则链接将保持`}<inlineCode parentName="p">{`unhealthy`}</inlineCode>{`的状态。`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
