  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'DNS提供商'
};
export const metadata = {
  "id": "rke/config-options/add-ons/dns/_index",
  "title": "DNS提供商",
  "description": "## 概述",
  "source": "@site/docs/rke/config-options/add-ons/dns/_index.md",
  "permalink": "/docs/rke/config-options/add-ons/dns/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rke/config-options/add-ons/dns/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '概述',
  id: '概述',
  children: []
}, {
  value: 'NodeLocal DNS',
  id: 'nodelocal-dns',
  children: [{
    value: '先决条件',
    id: '先决条件',
    children: []
  }, {
    value: '配置 NodeLocal DNS',
    id: '配置-nodelocal-dns',
    children: []
  }, {
    value: '移除 NodeLocal DNS',
    id: '移除-nodelocal-dns',
    children: []
  }]
}, {
  value: 'CoreDNS',
  id: 'coredns',
  children: [{
    value: '调度 CoreDNS',
    id: '调度-coredns',
    children: []
  }, {
    value: '配置 CoreDNS',
    id: '配置-coredns',
    children: []
  }]
}, {
  value: 'kube-dns',
  id: 'kube-dns',
  children: [{
    value: '调度 kube-dns',
    id: '调度-kube-dns',
    children: []
  }, {
    value: '配置 kube-dns',
    id: '配置-kube-dns',
    children: []
  }]
}, {
  value: '禁用 DNS provider 的 Deployment',
  id: '禁用-dns-provider-的-deployment',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "概述"
    }}>{`概述`}</h2>
    <p>{`RKE 提供了以下三种 DNS 提供商，作为附加组件部署：`}</p>
    <ul>
      <li parentName="ul">{`NodeLocal DNS`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://coredns.io"
        }}>{`CoreDNS`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/kubernetes/dns"
        }}>{`kube-dns`}</a></li>
    </ul>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`RKE 版本`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Kubernetes 版本`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`默认 DNS 提供商`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v0.2.5 及以上`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.14.0 及以上`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`CoreDNS`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v0.2.5 及以上`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.13.x 及以下`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-dns`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v0.2.4 及以下`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`无版本要求`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-dns`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.1.0 及以上`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.15.11+、v1.16.8 和 v1.17.4+`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`NodeLocal DNS`}</td>
        </tr>
      </tbody>
    </table>
    <p>{`在 RKE v0.2.5 及更新版本中，使用 Kubernetes 1.14 及以上版本时，CoreDNS 是默认 DNS 提供商。如果使 RKE 版本低于 v0.2.5，则 kube-dns 是默认 DNS 提供商。`}</p>
    <h2 {...{
      "id": "nodelocal-dns"
    }}>{`NodeLocal DNS`}</h2>
    <h3 {...{
      "id": "先决条件"
    }}>{`先决条件`}</h3>
    <p>{`启用 NodeLocal DNS 选项需要满足以下条件：`}</p>
    <ul>
      <li parentName="ul">{`RKE 版本为 v1.1.0 或更新版本`}</li>
      <li parentName="ul">{`Kubernetes 版本为 v1.15.11+、v1.16.8+或 v1.17.4+。`}</li>
    </ul>
    <p>{`NodeLocal DNS 是一个额外的组件，可以部署在每个节点上，提高 DNS 性能。由于它不能替代`}<inlineCode parentName="p">{`provider`}</inlineCode>{`参数，所以仍然需要为每个节点配置一个可用的 DNS 提供商。有关 NodeLocal DNS 工作原理的更多信息，请参见`}<a parentName="p" {...{
        "href": "https://kubernetes.io/zh/docs/tasks/administer-cluster/nodelocaldns/"
      }}>{`在 Kubernetes 集群中使用 NodeLocal DNS Cache`}</a>{`。`}</p>
    <h3 {...{
      "id": "配置-nodelocal-dns"
    }}>{`配置 NodeLocal DNS`}</h3>
    <p>{`使用`}<inlineCode parentName="p">{`ip_address`}</inlineCode>{`参数配置每台主机监听的链路本地 IP 地址，请确保这个 IP 地址在主机上没有被占用。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`dns:
  provider: coredns
  nodelocal:
    ip_address: "169.254.20.10"
`}</code></pre>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`说明：`}</strong>{`当在现有集群上启用 NodeLocal DNS 时，当前正在运行的 pod 不会被修改，更新后的`}<inlineCode parentName="p">{`/etc/resolv.conf`}</inlineCode>{`配置只对启用 NodeLocal DNS 后启动的 pod 生效。`}</p>
    </blockquote>
    <h3 {...{
      "id": "移除-nodelocal-dns"
    }}>{`移除 NodeLocal DNS`}</h3>
    <p>{`删除`}<inlineCode parentName="p">{`ip_address`}</inlineCode>{`值就会将从集群中删除 NodeLocal DNS。`}</p>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`说明：`}</strong>{` 删除 NodeLocal DNS 时， 可能会对 DNS 造成干扰。更新后的`}<inlineCode parentName="p">{`/etc/resolv.conf`}</inlineCode>{`配置仅对删除 NodeLocal DNS 后启动的 pod 生效。在一般情况下，使用默认`}<inlineCode parentName="p">{`dnsPolicy: ClusterFirst`}</inlineCode>{`将需要重新部署。`}</p>
    </blockquote>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`说明：`}</strong>{` 如果您从一个 DNS 提供商切换到另一个 DNS 提供商，在部署新的 DNS 提供商之前，现有的 DNS 提供商会被删除。`}</p>
    </blockquote>
    <h2 {...{
      "id": "coredns"
    }}>{`CoreDNS`}</h2>
    <p><em parentName="p">{`v0.2.5 及更新版本可用`}</em></p>
    <p>{`CoreDNS 只能在 Kubernetes v1.14.0 及以上版本上使用。`}</p>
    <p>{`RKE 将 CoreDNS 部署为一个默认副本为 1 的 Deployment，该 pod 由`}<inlineCode parentName="p">{`coredns`}</inlineCode>{`一个容器组成。RKE 也会将 coredns-autoscaler 部署为 Deployment，使用核心数量和节点的数量来扩展 coredns Deployment。详情请参考`}<a parentName="p" {...{
        "href": "https://github.com/kubernetes-incubator/cluster-proportional-autoscaler#linear-mode"
      }}>{`Linear Mode`}</a>{`。`}</p>
    <p>{`CoreDNS 使用的镜像在`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/system-images/_index"
      }}><inlineCode parentName="a">{`system_images`}</inlineCode></a>{`中。对于每个 Kubernetes 版本，都有与 CoreDNS 相关联的默认镜像，但这些镜像可以通过更改`}<inlineCode parentName="p">{`system_images`}</inlineCode>{`中的镜像标签来覆盖。`}</p>
    <h3 {...{
      "id": "调度-coredns"
    }}>{`调度 CoreDNS`}</h3>
    <p>{`如果只想在特定的节点上部署 CoreDNS pod ，可以在`}<inlineCode parentName="p">{`dns`}</inlineCode>{`部分设置一个`}<inlineCode parentName="p">{`node_selector`}</inlineCode>{`。`}<inlineCode parentName="p">{`node_selector`}</inlineCode>{`中的标签需要与要部署 CoreDNS pod 的节点上的标签相匹配。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`nodes:
  - address: 1.1.1.1
    role: [controlplane, worker, etcd]
    user: root
    labels:
      app: dns

dns:
  provider: coredns
  node_selector:
    app: dns
`}</code></pre>
    <h3 {...{
      "id": "配置-coredns"
    }}>{`配置 CoreDNS`}</h3>
    <h4 {...{
      "id": "上游名称服务器"
    }}>{`上游名称服务器`}</h4>
    <p>{`默认情况下，CoreDNS 使用主机配置的命名服务器（通常保存在`}<inlineCode parentName="p">{`/etc/resolv.conf`}</inlineCode>{`路径下）来解析外部查询。如果想配置特定的上游名称服务器，可以使用`}<inlineCode parentName="p">{`upstreamnameservers`}</inlineCode>{`指令。`}</p>
    <p>{`设置 `}<inlineCode parentName="p">{`upstreamnameservers`}</inlineCode>{`时，还需要同步修改`}<inlineCode parentName="p">{`provider`}</inlineCode>{`配置。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`dns:
  provider: coredns
  upstreamnameservers:
    - 1.1.1.1
    - 8.8.4.4
`}</code></pre>
    <h2 {...{
      "id": "kube-dns"
    }}>{`kube-dns`}</h2>
    <p>{`RKE 将 kube-dns 部署为一个默认副本为 1 的 Deployment，该 pod 由`}<inlineCode parentName="p">{`kubedns`}</inlineCode>{`、`}<inlineCode parentName="p">{`dnsmasq`}</inlineCode>{`和`}<inlineCode parentName="p">{`sidecar`}</inlineCode>{`共 3 个容器组成。RKE 也将 kube-dns-autoscaler 部署为 Deployment，通过使用核心和节点数量来扩展 kube-dns Deployment。详情请参考`}<a parentName="p" {...{
        "href": "https://github.com/kubernetes-incubator/cluster-proportional-autoscaler#linear-mode"
      }}>{`Linear Mode`}</a>{`。`}</p>
    <p>{`kube-dns 使用的镜像在`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/system-images/_index"
      }}><inlineCode parentName="a">{`system_images`}</inlineCode></a>{`下。对于每个 Kubernetes 版本，都有与 kube-dns 相关联的默认镜像，但这些图片可以通过更改`}<inlineCode parentName="p">{`system_images`}</inlineCode>{`中的镜像标签来覆盖。`}</p>
    <h3 {...{
      "id": "调度-kube-dns"
    }}>{`调度 kube-dns`}</h3>
    <p><em parentName="p">{`v0.2.0 及更新版本可用`}</em></p>
    <p>{`如果只想在特定的节点上部署 kube-dns pod，可以在`}<inlineCode parentName="p">{`dns`}</inlineCode>{`部分设置一个`}<inlineCode parentName="p">{`node_selector`}</inlineCode>{`。`}<inlineCode parentName="p">{`node_selector`}</inlineCode>{`中的标签需要与要部署 CoreDNS pod 的节点上的标签相匹配。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`nodes:
  - address: 1.1.1.1
    role: [controlplane, worker, etcd]
    user: root
    labels:
      app: dns

dns:
  provider: kube-dns
  node_selector:
    app: dns
`}</code></pre>
    <h3 {...{
      "id": "配置-kube-dns"
    }}>{`配置 kube-dns`}</h3>
    <h4 {...{
      "id": "上游名称服务器-1"
    }}>{`上游名称服务器`}</h4>
    <p><em parentName="p">{`v0.2.0 及更新版本可用`}</em></p>
    <p>{`默认情况下，kube-dns 使用主机配置的命名服务器（通常保存在`}<inlineCode parentName="p">{`/etc/resolv.conf`}</inlineCode>{`路径下）来解析外部查询。如果想配置特定的上游名称服务器，可以使用`}<inlineCode parentName="p">{`upstreamnameservers`}</inlineCode>{`指令。`}</p>
    <p>{`设置 `}<inlineCode parentName="p">{`upstreamnameservers`}</inlineCode>{`时，还需要同步修改`}<inlineCode parentName="p">{`provider`}</inlineCode>{`配置。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`dns:
  provider: kube-dns
  upstreamnameservers:
    - 1.1.1.1
    - 8.8.4.4
`}</code></pre>
    <h2 {...{
      "id": "禁用-dns-provider-的-deployment"
    }}>{`禁用 DNS provider 的 Deployment`}</h2>
    <p><em parentName="p">{`v0.2.0 及更新版本可用`}</em></p>
    <p>{`您可以在集群配置中的 dns`}<inlineCode parentName="p">{`provider`}</inlineCode>{`设置为`}<inlineCode parentName="p">{`none`}</inlineCode>{`，禁用默认的 DNS 提供商。这个操作会阻止 pods 在集群中进行名称解析。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`dns:
  provider: none
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
