  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '身份验证',
	description: 'Rancher 向 Kubernetes 添加的关键特性之一是集中式用户身份验证。该特性允许您的用户使用一组凭证对任何 Rancher 管理的 Kubernetes 集群进行身份验证。这种集中式的用户身份验证是使用 Rancher 身份验证代理完成的，该代理与 Rancher 的其他组件一起安装。这个代理验证您的用户，并使用一个服务帐户将用户请求转发到您的 Kubernetes 集群。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'系统管理员指南',
		'登录认证',
		'身份验证'
	]
};
export const metadata = {
  "id": "rancher2/admin-settings/authentication/_index",
  "title": "身份验证",
  "description": "Rancher 向 Kubernetes 添加的关键特性之一是集中式用户身份验证。该特性允许您的用户使用一组凭证对任何 Rancher 管理的 Kubernetes 集群进行身份验证。这种集中式的用户身份验证是使用 Rancher 身份验证代理完成的，该代理与 Rancher 的其他组件一起安装。这个代理验证您的用户，并使用一个服务帐户将用户请求转发到您的 Kubernetes 集群。",
  "source": "@site/docs/rancher2/admin-settings/authentication/_index.md",
  "permalink": "/docs/rancher2/admin-settings/authentication/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/admin-settings/authentication/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "rancher2",
  "previous": {
    "title": "全局私有镜像库",
    "permalink": "/docs/rancher2/admin-settings/config-private-registry/_index"
  },
  "next": {
    "title": "用户和用户组",
    "permalink": "/docs/rancher2/admin-settings/authentication/user-groups/_index"
  }
};
