  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '关于监控'
};
export const metadata = {
  "id": "octopus/monitoring/_index",
  "title": "关于监控",
  "description": "Octopus 基于[sigs.k8s.io/controller-runtime](https://github.com/kubernetes-sigs/controller-runtime)上搭建，因此某些指标与控制器运行时和[client-go](https://github.com/kubernetes/client-go)相关。 同时[github.com/prometheus/client_golang](https://github.com/prometheus/client_golang)为[Go runtime](https://golang.org/pkg/runtime/)提供了一些指标和过程状态。",
  "source": "@site/docs/octopus/monitoring/_index.md",
  "permalink": "/docs/octopus/monitoring/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/octopus/monitoring/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "octopus",
  "previous": {
    "title": "Octopus 开发指南",
    "permalink": "/docs/octopus/develop/_index"
  },
  "next": {
    "title": "Octopus UI",
    "permalink": "/docs/octopus/octopus-ui/_index"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: '指标类别',
  id: '指标类别',
  children: [{
    value: 'Controller Runtime 指标对照表',
    id: 'controller-runtime-指标对照表',
