  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '默认的Kubernetes服务'
};
export const metadata = {
  "id": "rke/config-options/services/_index",
  "title": "默认的Kubernetes服务",
  "description": "为了部署 Kubernetes，RKE 在节点上的 Docker 容器中部署了几个核心组件或服务。根据节点的角色，部署的容器可能不同。",
  "source": "@site/docs/rke/config-options/services/_index.md",
  "permalink": "/docs/rke/config-options/services/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rke/config-options/services/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'etcd',
  id: 'etcd',
  children: []
}, {
  value: 'Kubernetes API Server',
  id: 'kubernetes-api-server',
  children: [{
    value: 'Kubernetes API Server 选项',
    id: 'kubernetes-api-server-选项',
    children: []
  }]
}, {
  value: 'Kubernetes Controller Manager',
  id: 'kubernetes-controller-manager',
  children: [{
    value: 'Kubernetes Controller Manager 选项',
    id: 'kubernetes-controller-manager-选项',
    children: []
  }]
}, {
  value: 'Kubelet',
  id: 'kubelet',
  children: [{
    value: 'Kubelet 选项',
    id: 'kubelet-选项',
    children: []
  }, {
    value: 'Kubelet Serving Certificate 需求',
    id: 'kubelet-serving-certificate-需求',
    children: []
  }]
}, {
  value: 'Kubernetes Scheduler',
  id: 'kubernetes-scheduler',
  children: []
}, {
  value: 'Kubernetes Network Proxy',
  id: 'kubernetes-network-proxy',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`为了部署 Kubernetes，RKE 在节点上的 Docker 容器中部署了几个核心组件或服务。根据节点的角色，部署的容器可能不同。`}</p>
    <p>{`所有服务都支持额外的`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/services/services-extras/_index"
      }}>{`自定义参数、Docker 挂载绑定和额外的环境变量`}</a>{`。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`组件`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`cluster.yml 中的服务名`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`etcd`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`etcd`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-apiserver`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`kube-api`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-controller-manager`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`kube-controller`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kubelet`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`kubelet`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-scheduler`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`scheduler`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`kube-proxy`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`kubeproxy`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "etcd"
    }}>{`etcd`}</h2>
    <p>{`Kubernetes 使用`}<a parentName="p" {...{
        "href": "https://etcd.io/"
      }}>{`etcd`}</a>{`作为集群状态和数据的存储。etcd 是一个可靠的、一致的、分布式的键值存储。`}</p>
    <p>{`RKE 支持在单节点模式或 HA 集群模式下运行 etcd。它还支持向集群中添加和移除 etcd 节点。`}</p>
    <p>{`您可以启用 etcd 来`}<a parentName="p" {...{
        "href": "/docs/rke/etcd-snapshots/_index"
      }}>{`拍摄循环快照`}</a>{`。`}</p>
    <p>{`默认情况下，RKE 会部署一个新的 etcd 服务，但您也可以使用`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/services/external-etcd/_index"
      }}>{`外部 etcd 服务`}</a>{`来运行 Kubernetes。`}</p>
    <h2 {...{
      "id": "kubernetes-api-server"
    }}>{`Kubernetes API Server`}</h2>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`Rancher 2 用户注意`}</strong>{`如果您在创建`}<a parentName="p" {...{
          "href": "/docs/rancher2/cluster-provisioning/rke-clusters/options/_index"
        }}>{`Rancher Launched Kubernetes`}</a>{`配置集群选项时，创建`}<a parentName="p" {...{
          "href": "/docs/rancher2/cluster-provisioning/rke-clusters/_index"
        }}>{`Rancher Launched Kubernetes`}</a>{`时，服务名称应只包含下划线：`}<inlineCode parentName="p">{`kube_api`}</inlineCode>{`。这只适用于 Rancher v2.0.5 和 v2.0.6。`}</p>
    </blockquote>
    <p><a parentName="p" {...{
        "href": "https://kubernetes.io/docs/reference/command-line-tools-reference/kube-apiserver/"
      }}>{`Kubernetes API`}</a>{` REST 服务，处理所有 Kubernetes 对象的请求和数据，并为所有其他 Kubernetes 组件提供共享状态。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`services:
  kube-api:
    # 在Kubernetes上创建的服务的IP范围。
    # 必须与kube-controller中的service_cluster_ip_range匹配
    service_cluster_ip_range: 10.43.0.0/16
    # 为NodePort服务提供不同的端口范围
    service_node_port_range: 30000-32767
    pod_security_policy: false
    # 启用AlwaysPullImagesAdmission controller插件
    # v0.2.0或更新版本可用
    always_pull_images: false
    secrets_encryption_config:
      enabled: true
`}</code></pre>
    <h3 {...{
      "id": "kubernetes-api-server-选项"
    }}>{`Kubernetes API Server 选项`}</h3>
    <p>{`RKE 支持`}<inlineCode parentName="p">{`kube-api`}</inlineCode>{`服务的以下选项。`}</p>
    <ul>
      <li parentName="ul"><strong parentName="li">{`服务集群 IP 范围`}</strong>{` (`}<inlineCode parentName="li">{`service_cluster_ip_range`}</inlineCode>{`) - 这是将分配给 Kubernetes 上创建的服务的虚拟 IP 地址。默认情况下，服务集群 IP 范围是`}<inlineCode parentName="li">{`10.43.0.0/16`}</inlineCode>{`。如果您改变了这个值，那么也必须在 Kubernetes 控制器管理器（`}<inlineCode parentName="li">{`kube-controller`}</inlineCode>{`）上设置相同的值。`}</li>
      <li parentName="ul"><strong parentName="li">{`节点端口范围`}</strong>{` (`}<inlineCode parentName="li">{`service_node_port_range`}</inlineCode>{`) - 使用`}<a parentName="li" {...{
          "href": "https://kubernetes.io/docs/concepts/services-networking/service/#publishing-services-service-types"
        }}>{`type`}</a>{` `}<inlineCode parentName="li">{`NodePort`}</inlineCode>{`创建的 Kubernetes 服务的端口范围。默认情况下，端口范围是`}<inlineCode parentName="li">{`30000-32767`}</inlineCode>{`。`}</li>
      <li parentName="ul"><strong parentName="li">{`Pod 安全策略`}</strong>{` (`}<inlineCode parentName="li">{`pod_security_policy`}</inlineCode>{`) - 启用`}<a parentName="li" {...{
          "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/"
        }}>{`Kubernetes Pod 安全策略`}</a>{`的选项。默认情况下，我们不会启用 pod 安全策略，因为它被设置为`}<inlineCode parentName="li">{`false`}</inlineCode>{`。`}<blockquote parentName="li">
          <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{`如果您将`}<inlineCode parentName="p">{`pod_security_policy`}</inlineCode>{`值设置为`}<inlineCode parentName="p">{`true`}</inlineCode>{`，RKE 将配置一个开放的策略，允许任何 pod 在集群上工作。您需要配置您自己的策略来充分利用 PSP。`}</p>
        </blockquote></li>
      <li parentName="ul"><strong parentName="li">{`拉取镜像`}</strong>{` (`}<inlineCode parentName="li">{`always_pull_images`}</inlineCode>{`) - 启用`}<inlineCode parentName="li">{`AlwaysPullImages`}</inlineCode>{` Admission 控制器插件。启用`}<inlineCode parentName="li">{`AlwaysPullImages`}</inlineCode>{`是一个安全的最佳实践。它强制 Kubernetes 与远程图像注册表验证图像和拉动凭证。本地图像层缓存仍将被使用，但它确实会在启动容器拉取和比较图像哈希时增加一点开销。`}<em parentName="li">{`注：从 v0.2.0 开始提供`}</em>{`。`}</li>
      <li parentName="ul"><strong parentName="li">{`Secrets Encryption Config`}</strong>{` (`}<inlineCode parentName="li">{`secrets_encryption_config`}</inlineCode>{`) - 管理 Kubernetes 静态数据加密。文档化`}<a parentName="li" {...{
          "href": "/docs/rke/config-options/secrets-encryption/_index"
        }}>{`这里`}</a></li>
    </ul>
    <h2 {...{
      "id": "kubernetes-controller-manager"
    }}>{`Kubernetes Controller Manager`}</h2>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`Rancher 2 用户注意`}</strong>{`如果您在创建`}<a parentName="p" {...{
          "href": "/docs/rancher2/cluster-provisioning/rke-clusters/options/_index"
        }}>{`Rancher Launched Kubernetes`}</a>{`时，在创建`}<a parentName="p" {...{
          "href": "/docs/rancher2/cluster-provisioning/rke-clusters/options/_index"
        }}>{`Rancher Launched Kubernetes`}</a>{`时，服务名称应只包含下划线：`}<inlineCode parentName="p">{`kube_controller`}</inlineCode>{`。这只适用于 Rancher v2.0.5 和 v2.0.6。`}</p>
    </blockquote>
    <p><a parentName="p" {...{
        "href": "https://kubernetes.io/docs/reference/command-line-tools-reference/kube-controller-manager/"
      }}>{`Kubernetes 控制器管理器`}</a>{`服务是负责运行 Kubernetes 主控制循环的组件。控制器管理器通过 Kubernetes API 服务器监控集群所需的状态，并对当前状态进行必要的修改，以达到所需的状态。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`services:
  kube-controller:
    # CIDR pool used to assign IP addresses to pods in the cluster
    cluster_cidr: 10.42.0.0/16
    # IP range for any services created on Kubernetes
    # This must match the service_cluster_ip_range in kube-api
    service_cluster_ip_range: 10.43.0.0/16
`}</code></pre>
    <h3 {...{
      "id": "kubernetes-controller-manager-选项"
    }}>{`Kubernetes Controller Manager 选项`}</h3>
    <p>{`RKE 支持`}<inlineCode parentName="p">{`kube-controller`}</inlineCode>{`服务的以下选项。`}</p>
    <ul>
      <li parentName="ul"><strong parentName="li">{`集群 CIDR`}</strong>{` (`}<inlineCode parentName="li">{`cluster_cidr`}</inlineCode>{`) - 用于为集群中的 pod 分配 IP 地址的 CIDR 池。默认情况下，集群中的每个节点都会从该池中分配一个`}<inlineCode parentName="li">{`/24`}</inlineCode>{`网络，用于分配 pod IP。该选项的默认值是`}<inlineCode parentName="li">{`10.42.0.0/16`}</inlineCode>{`。`}</li>
      <li parentName="ul"><strong parentName="li">{`服务集群 IP 范围`}</strong>{` (`}<inlineCode parentName="li">{`service_cluster_ip_range`}</inlineCode>{`) - 这是将分配给 Kubernetes 上创建的服务的虚拟 IP 地址。默认情况下，服务集群 IP 范围是`}<inlineCode parentName="li">{`10.43.0.0/16`}</inlineCode>{`。如果您改变了这个值，那么也必须在 Kubernetes API 服务器（`}<inlineCode parentName="li">{`kube-api`}</inlineCode>{`）上设置相同的值。`}</li>
    </ul>
    <h2 {...{
      "id": "kubelet"
    }}>{`Kubelet`}</h2>
    <p><a parentName="p" {...{
        "href": "https://kubernetes.io/docs/reference/command-line-tools-reference/kubelet/"
      }}>{`kubelet`}</a>{`服务作为 Kubernetes 的 "节点代理"。它运行在 RKE 部署的所有节点上，并让 Kubernetes 有能力管理节点上的容器运行时。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`services:
  kubelet:
    # Base domain for the cluster
    cluster_domain: cluster.local
    # IP address for the DNS service endpoint
    cluster_dns_server: 10.43.0.10
    # Fail if swap is on
    fail_swap_on: false
    # Generate per node serving certificate
    generate_serving_certificate: false
`}</code></pre>
    <h3 {...{
      "id": "kubelet-选项"
    }}>{`Kubelet 选项`}</h3>
    <p>{`RKE 支持 "kubelet "服务的以下选项。`}</p>
    <ul>
      <li parentName="ul"><strong parentName="li">{`集群域`}</strong>{` (`}<inlineCode parentName="li">{`cluster_domain`}</inlineCode>{`) - 集群的`}<a parentName="li" {...{
          "href": "https://kubernetes.io/docs/concepts/services-networking/dns-pod-service/"
        }}>{`基本域`}</a>{`。集群上创建的所有服务和 DNS 记录。默认情况下，域被设置为`}<inlineCode parentName="li">{`cluster.local`}</inlineCode>{`。`}</li>
      <li parentName="ul"><strong parentName="li">{`集群 DNS 服务器`}</strong>{` (`}<inlineCode parentName="li">{`cluster_dns_server`}</inlineCode>{`) - 分配给集群内 DNS 服务端点的 IP 地址。DNS 查询将被发送到这个 IP 地址，该地址被 KubeDNS 使用。这个选项的默认值是`}<inlineCode parentName="li">{`10.43.0.10`}</inlineCode>{`。`}</li>
      <li parentName="ul"><strong parentName="li">{`Fail if Swap is On`}</strong>{` (`}<inlineCode parentName="li">{`fail_swap_on`}</inlineCode>{`) - 在 Kubernetes 中，如果节点上启用了 swap，kubelet 的默认行为是`}<strong parentName="li">{`失败`}</strong>{`。RKE 不会遵循这个默认值，而是允许在启用 swap 的节点上进行部署。默认情况下，这个值是`}<inlineCode parentName="li">{`false`}</inlineCode>{`。如果您想恢复到默认的 kubelet 行为，请将此选项设置为 "true"。`}</li>
      <li parentName="ul"><strong parentName="li">{`Generate Serving Certificate`}</strong>{` (`}<inlineCode parentName="li">{`generate_serving_certificate`}</inlineCode>{`) - 为 kubelet 生成一个由`}<inlineCode parentName="li">{`kube-ca`}</inlineCode>{`证书颁发机构签署的证书作为服务器证书。这个选项的默认值是`}<inlineCode parentName="li">{`false`}</inlineCode>{`。在启用这个选项之前，请参考下文的 Kubelet Serving Certificate 需求。`}</li>
    </ul>
    <h3 {...{
      "id": "kubelet-serving-certificate-需求"
    }}>{`Kubelet Serving Certificate 需求`}</h3>
    <p>{`如果在`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`中为一个或多个节点配置了`}<inlineCode parentName="p">{`hostname_override`}</inlineCode>{`，请确保在`}<inlineCode parentName="p">{`address`}</inlineCode>{`中配置了正确的 IP 地址（以及`}<inlineCode parentName="p">{`internal_address`}</inlineCode>{`中的内部地址），以确保生成的证书包含正确的 IP 地址。`}</p>
    <p>{`一个错误的例子是，在 EC2 实例中，如果在`}<inlineCode parentName="p">{`address`}</inlineCode>{`中配置了公共 IP 地址，并且使用了`}<inlineCode parentName="p">{`hostname_override`}</inlineCode>{`，那么`}<inlineCode parentName="p">{`kube-apiserver`}</inlineCode>{`和`}<inlineCode parentName="p">{`kubelet`}</inlineCode>{`之间的连接将失败，因为`}<inlineCode parentName="p">{`kubelet`}</inlineCode>{`将在私有 IP 地址上被联系，生成的证书将无效（将看到错误`}<inlineCode parentName="p">{`x509：证书对 value_in_address 有效，而不是 private_ip`}</inlineCode>{`）。解决方法是在`}<inlineCode parentName="p">{`internal_address`}</inlineCode>{`中提供内部 IP 地址。`}</p>
    <p>{`关于主机覆盖的更多信息，请参考`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/nodes/_index"
      }}>{`节点配置页`}</a>{`。`}</p>
    <h2 {...{
      "id": "kubernetes-scheduler"
    }}>{`Kubernetes Scheduler`}</h2>
    <p><a parentName="p" {...{
        "href": "https://kubernetes.io/docs/reference/command-line-tools-reference/kube-scheduler/"
      }}>{`Kubernetes Scheduler`}</a>{`服务负责根据各种配置、指标、资源需求和工作负载的特定要求来调度集群工作负载。`}</p>
    <p>{`目前，RKE 并不支持`}<inlineCode parentName="p">{`scheduler`}</inlineCode>{`服务的任何特定选项。`}</p>
    <h2 {...{
      "id": "kubernetes-network-proxy"
    }}>{`Kubernetes Network Proxy`}</h2>
    <p><a parentName="p" {...{
        "href": "https://kubernetes.io/docs/reference/command-line-tools-reference/kube-proxy/"
      }}>{`Kubernetes 网络代理`}</a>{`服务运行在所有节点上，管理 Kubernetes 创建的 TCP/UDP 端口的端点。`}</p>
    <p>{`目前，RKE 不支持 "kubeproxy "服务的任何特定选项。`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
