		'集群访问控制',
		'webhooks'
	]
};
export const metadata = {
  "id": "rancher2/cluster-admin/webhooks/_index",
  "title": "Webhooks",
  "description": "## 1. 简述",
  "source": "@site/docs/rancher2/cluster-admin/webhooks/_index.md",
  "permalink": "/docs/rancher2/cluster-admin/webhooks/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/cluster-admin/webhooks/_index.md",
