export const rightToc = [{
  value: '1. 简述',
  id: '1-简述',
  children: []
}, {
  value: '2. 准备配置文件',
  id: '2-准备配置文件',
  children: []
}, {
  value: '3. webhooks 安装',
  id: '3-webhooks-安装',
  children: []
}, {
  value: '4. webhooks 触发地址',
  id: '4-webhooks-触发地址',
  children: []
}, {
  value: '5. 配置仓库触发',
  id: '5-配置仓库触发',
  children: [{
    value: '5.1. Aliyun',
    id: '51-aliyun',
    children: []
  }, {
    value: '5.2. Docker',
    id: '52-docker',
    children: []
  }, {
    value: '5.3. 自定义 webhooks',
    id: '53-自定义-webhooks',
    children: []
