}, {
  value: '附加IAM策略到角色',
  id: '附加iam策略到角色',
  children: []
}, {
  value: '将角色的ARN添加到注释到serviceaccount',
  id: '将角色的arn添加到注释到serviceaccount',
  children: []
}, {
  value: '部署alb-ingress-controller',
  id: '部署alb-ingress-controller',
