  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'TLS 设置',
	descripition: '在 Rancher v2.1.7 中，默认 TLS 配置已更改为仅接受 TLS 1.2 和安全 TLS 密码套件。不支持 TLS 1.3 和 TLS 1.3 专用密码套件。通过将环境变量传递到 Rancher Server 容器来启用和配置审计日志。请参阅以下内容以启用安装。'
};
export const metadata = {
  "id": "rancher2/installation/options/tls-settings/_index",
  "title": "TLS 设置",
  "description": "_自 v2.1.7 起可用_",
  "source": "@site/docs/rancher2/installation/options/tls-settings/_index.md",
  "permalink": "/docs/rancher2/installation/options/tls-settings/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/installation/options/tls-settings/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "rancher2",
  "previous": {
    "title": "启用API审计日志记录系统事件",
    "permalink": "/docs/rancher2/installation/options/api-audit-log/_index"
  },
  "next": {
    "title": "通过 firewalld 放行端口",
    "permalink": "/docs/rancher2/installation/options/firewall/_index"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: '配置 TLS 设置',
  id: '配置-tls-设置',
  children: []
}, {
  value: 'TLS 设置',
  id: 'tls-设置',
  children: []
}, {
  value: '旧版配置',
  id: '旧版配置',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><em parentName="p">{`自 v2.1.7 起可用`}</em></p>
    <p>{`在 Rancher v2.1.7 中，默认 TLS 配置已更改为`}<strong parentName="p">{`仅接受`}</strong>{`TLS 1.2 和安全 TLS 密码套件，`}<strong parentName="p">{`不支持`}</strong>{`TLS 1.3 和 TLS 1.3 专用密码套件。`}</p>
    <h2 {...{
      "id": "配置-tls-设置"
    }}>{`配置 TLS 设置`}</h2>
    <p>{`可通过将环境变量传递到 Rancher Server 容器来启用和配置审计日志。请参阅以下内容以启用安装。`}</p>
    <ul>
      <li parentName="ul">
        <p parentName="li"><a parentName="p" {...{
            "href": "/docs/rancher2/installation/other-installation-methods/single-node-docker/_index"
          }}>{`使用 Docker 在单个节点上安装 Rancher`}</a></p>
      </li>
      <li parentName="ul">
        <p parentName="li"><a parentName="p" {...{
            "href": "/docs/rancher2/installation/options/chart-options/_index"
          }}>{`在 Kubernetes 上安装 Rancher`}</a></p>
      </li>
    </ul>
    <h2 {...{
      "id": "tls-设置"
    }}>{`TLS 设置`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`描述`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`默认值`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`可用选项`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`CATTLE_TLS_MIN_VERSION`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`最低 TLS 版本`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`1.2`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`1.0`}</inlineCode>{`, `}<inlineCode parentName="td">{`1.1`}</inlineCode>{`, `}<inlineCode parentName="td">{`1.2`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`CATTLE_TLS_CIPHERS`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`允许的 TLS 密码套件`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`TLS_ECDHE_ECDSA_WITH_AES_128_GCM_SHA256,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_ECDSA_WITH_AES_256_GCM_SHA384,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_ECDSA_WITH_CHACHA20_POLY1305,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_RSA_WITH_AES_128_GCM_SHA256,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_RSA_WITH_AES_256_GCM_SHA384,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_RSA_WITH_CHACHA20_POLY1305`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`请参阅 `}<a parentName="td" {...{
              "href": "https://golang.org/pkg/crypto/tls/#pkg-constants"
            }}>{`Golang tls 常量`}</a></td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "旧版配置"
    }}>{`旧版配置`}</h2>
    <p>{`如果您需要按照 Rancher v2.1.7 之前的方式配置 TLS，请使用以下设置：`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`旧版的值`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`CATTLE_TLS_MIN_VERSION`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`1.0`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`CATTLE_TLS_CIPHERS`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`TLS_ECDHE_RSA_WITH_AES_128_GCM_SHA256,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_ECDSA_WITH_AES_128_GCM_SHA256,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_RSA_WITH_AES_256_GCM_SHA384,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_ECDSA_WITH_AES_256_GCM_SHA384,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_RSA_WITH_CHACHA20_POLY1305,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_ECDSA_WITH_CHACHA20_POLY1305,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_RSA_WITH_AES_128_CBC_SHA,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_ECDSA_WITH_AES_128_CBC_SHA,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_RSA_WITH_AES_256_CBC_SHA,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_ECDSA_WITH_AES_256_CBC_SHA,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_RSA_WITH_AES_128_GCM_SHA256,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_RSA_WITH_AES_256_GCM_SHA384,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_RSA_WITH_AES_128_CBC_SHA,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_RSA_WITH_AES_256_CBC_SHA,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_ECDHE_RSA_WITH_3DES_EDE_CBC_SHA,`}</inlineCode><br /><inlineCode parentName="td">{`TLS_RSA_WITH_3DES_EDE_CBC_SHA`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
