  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Pod 安全策略',
	description: 'Pod 安全策略是控制 Pod 安全的规范（如是否可以使用 root 权限等）的对象。如果 Pod 不符合 PSP 中指定的条件，Kubernetes 将不允许其启动，并且 Rancher 中将显示错误消息Pod <NAME> is forbidden unable to validate...。RKE 元数据功能允许您在发布新版本的 Kubernetes 后立即为集群配置它们，而无需升级 Rancher。此功能对于使用 Kubernetes 的补丁版本非常有用，例如，如果您希望在仅支持 Kubernetes v1.14.6 的 Rancher Server 版本中，将业务集群升级到 Kubernetes v1.14.7。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'系统管理员指南',
		'Pod 安全策略'
	]
};
export const metadata = {
  "id": "rancher2/admin-settings/pod-security-policies/_index",
  "title": "Pod 安全策略",
  "description": "Pod 安全策略是控制 Pod 安全的规范（如是否可以使用 root 权限等）的对象。如果 Pod 不符合 PSP 中指定的条件，Kubernetes 将不允许其启动，并且 Rancher 中将显示错误消息Pod <NAME> is forbidden unable to validate...。RKE 元数据功能允许您在发布新版本的 Kubernetes 后立即为集群配置它们，而无需升级 Rancher。此功能对于使用 Kubernetes 的补丁版本非常有用，例如，如果您希望在仅支持 Kubernetes v1.14.6 的 Rancher Server 版本中，将业务集群升级到 Kubernetes v1.14.7。",
  "source": "@site/docs/rancher2/admin-settings/pod-security-policies/_index.md",
  "permalink": "/docs/rancher2/admin-settings/pod-security-policies/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/admin-settings/pod-security-policies/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "rancher2",
  "previous": {
    "title": "获取新的 Kubernetes 版本",
    "permalink": "/docs/rancher2/admin-settings/k8s-metadata/_index"
  },
  "next": {
    "title": "功能介绍",
    "permalink": "/docs/rancher2/admin-settings/rke-templates/_index"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: '概述',
  id: '概述',
  children: []
}, {
  value: '默认 Pod 安全策略',
  id: '默认-pod-安全策略',
  children: []
}, {
  value: '创建 Pod 安全策略',
  id: '创建-pod-安全策略',
  children: []
}, {
  value: '后续操作',
  id: '后续操作',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "概述"
    }}>{`概述`}</h2>
    <p><strong parentName="p">{`Pod 安全策略`}</strong>{` (PSP) 是控制 Pod 安全的规范（如是否可以使用 root 权限等）的对象。如果 Pod 不符合 PSP 中指定的条件，Kubernetes 将不允许其启动，并且 Rancher 中将显示错误消息`}<inlineCode parentName="p">{`Pod <NAME> is forbidden: unable to validate...`}</inlineCode>{`。`}</p>
    <blockquote>
      <p parentName="blockquote">{`注意：
给集群分配 Pod 安全策略的功能，仅在 RKE 集群中可用。`}</p>
    </blockquote>
    <ul>
      <li parentName="ul">
        <p parentName="li">{`您可以在集群或项目级别分配 PSP。`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li">{`PSP 通过继承的方式工作。`}</p>
        <ul parentName="li">
          <li parentName="ul">{`默认情况下，分配给集群的 PSP 由其项目以及添加到这些项目的任何命名空间继承。`}</li>
          <li parentName="ul"><strong parentName="li">{`例外：`}</strong>{` 无论 PSP 是分配给集群还是项目，未分配给项目的命名空间不会继承 PSP。由于这些命名空间没有 PSP，因此将工作负载部署到这些命名空间将失败，这是 Kubernetes 的默认行为。`}</li>
          <li parentName="ul">{`您可以通过直接向项目分配其他 PSP 来覆盖默认 PSP。`}</li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li">{`在分配 PSP 之前，集群或项目中已经在运行的任何工作负荷是否符合 PSP 的规定，将不会进行检查。需要克隆或升级工作负载以查看它们是否通过了 PSP。`}</p>
      </li>
    </ul>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{` 必须先在集群级别启用 PSP，然后才能将它们分配给项目。可以通过`}<a parentName="p" {...{
          "href": "/docs/rancher2/cluster-admin/editing-clusters/_index"
        }}>{`编辑集群`}</a>{`进行配置。`}</p>
    </blockquote>
    <p>{`在 `}<a parentName="p" {...{
        "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/"
      }}>{`Kubernetes 文档`}</a>{`中了解有关 Pod 安全策略的更多信息。`}</p>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`最佳实践：`}</strong>{` 在集群级别设置 Pod 安全策略。`}</p>
    </blockquote>
    <p>{`您可以使用 Rancher UI 创建 Pod 安全策略，而不必使用 YAML 文件。`}</p>
    <h2 {...{
      "id": "默认-pod-安全策略"
    }}>{`默认 Pod 安全策略`}</h2>
    <p><em parentName="p">{`自 v2.0.7 起可用`}</em></p>
    <p>{`Rancher 内置了两个默认的 Pod 安全策略（PSP）：`}<inlineCode parentName="p">{`受限`}</inlineCode>{`和`}<inlineCode parentName="p">{`不受限`}</inlineCode>{`策略。`}</p>
    <ul>
      <li parentName="ul">
        <p parentName="li">{`受限策略`}</p>
        <p parentName="li">{`该策略基于 Kubernetes `}<a parentName="p" {...{
            "href": "https://raw.githubusercontent.com/kubernetes/website/master/content/en/examples/policy/restricted-psp.yaml"
          }}>{`示例受限策略`}</a>{`。它极大地限制了可以将哪些类型的 Pod 部署到集群或项目中。这项策略：`}</p>
        <ul parentName="li">
          <li parentName="ul">{`阻止 Pod 以特权用户身份运行，并防止特权升级。`}</li>
          <li parentName="ul">{`验证服务器所需的安全性机制是否到位(例如，限制哪些卷可以被挂载，以及防止添加 root 补充组)。`}</li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li">{`不受限策略`}</p>
        <p parentName="li">{`该策略等效于在禁用 PSP 控制器的情况下运行 Kubernetes。对于可以将哪些 Pod 部署到集群或项目中，它没有任何限制`}</p>
      </li>
    </ul>
    <h2 {...{
      "id": "创建-pod-安全策略"
    }}>{`创建 Pod 安全策略`}</h2>
    <ol>
      <li parentName="ol">{`在`}<strong parentName="li">{`全局`}</strong>{`视图中，从主菜单中选择`}<strong parentName="li">{`安全性 > Pod 安全策略`}</strong>{`。然后单击`}<strong parentName="li">{`添加策略`}</strong>{`。`}</li>
    </ol>
    <p><strong parentName="p">{`步骤结果：`}</strong>{` 将打开`}<strong parentName="p">{`添加策略`}</strong>{`表单。`}</p>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">
        <p parentName="li">{`命名策略。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`填写表格的每个部分。请参阅下面 Kubernetes 文档的链接，以获取有关每个策略的作用的更多信息。`}</p>
        <ul parentName="li">
          <li parentName="ul">
            <p parentName="li">{`基本策略：`}</p>
            <ul parentName="li">
              <li parentName="ul"><a parentName="li" {...{
                  "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#privilege-escalation"
                }}>{`提升权限`}</a></li>
              <li parentName="ul"><a parentName="li" {...{
                  "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#host-namespaces"
                }}>{`主机空间`}</a></li>
              <li parentName="ul"><a parentName="li" {...{
                  "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#volumes-and-file-systems"
                }}>{`只读根文件系统`}</a></li>
            </ul>
          </li>
          <li parentName="ul">
            <p parentName="li"><a parentName="p" {...{
                "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#capabilities"
              }}>{`能力策略`}</a></p>
          </li>
          <li parentName="ul">
            <p parentName="li"><a parentName="p" {...{
                "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#volumes-and-file-systems"
              }}>{`卷策略`}</a></p>
          </li>
          <li parentName="ul">
            <p parentName="li"><a parentName="p" {...{
                "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#volumes-and-file-systems"
              }}>{`允许的主机路径策略`}</a></p>
          </li>
          <li parentName="ul">
            <p parentName="li"><a parentName="p" {...{
                "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#volumes-and-file-systems"
              }}>{`文件系统组策略`}</a></p>
          </li>
          <li parentName="ul">
            <p parentName="li"><a parentName="p" {...{
                "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#host-namespaces"
              }}>{`主机端口策略`}</a></p>
          </li>
          <li parentName="ul">
            <p parentName="li"><a parentName="p" {...{
                "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#users-and-groups"
              }}>{`允许的用户策略`}</a></p>
          </li>
          <li parentName="ul">
            <p parentName="li"><a parentName="p" {...{
                "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#selinux"
              }}>{`SELinux 策略`}</a></p>
          </li>
          <li parentName="ul">
            <p parentName="li"><a parentName="p" {...{
                "href": "https://kubernetes.io/docs/concepts/policy/pod-security-policy/#users-and-groups"
              }}>{`附加组策略`}</a></p>
          </li>
        </ul>
      </li>
    </ol>
    <h2 {...{
      "id": "后续操作"
    }}>{`后续操作`}</h2>
    <p>{`您可以在以下上下文中添加 Pod 安全策略（PSP)：`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/docs/rancher2/cluster-provisioning/rke-clusters/options/pod-security-policies/_index"
        }}>{`创建集群时`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/docs/rancher2/cluster-admin/editing-clusters/_index"
        }}>{`编辑现有集群时`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/docs/rancher2/cluster-admin/projects-and-namespaces/_index"
        }}>{`创建项目时`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/docs/rancher2/project-admin/_index"
        }}>{`编辑现有项目时`}</a></li>
    </ul>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{` 我们建议在集群和项目创建期间添加 PSP，而不是将 PSP 添加到现有的集群和项目中。`}</p>
    </blockquote>
    {
      /* links */
    }
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
