  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'API Tokens',
	description: '我们建议您在创建 API KEY 时，给它设置一个有效时间。但是一些 API Tokens 是永久有效(`ttl=0`)，除非删除它们，否则永不失效，并且 API Tokens 不会因更改用户密码而失效。您可以通过删除 API Tokens 或禁用用户来禁用它们。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'API',
		'API Tokens',
		'API指南',
		'API参考'
	]
};
export const metadata = {
  "id": "rancher2/api/api-tokens/_index",
  "title": "API Tokens",
  "description": "我们建议您在创建 API KEY 时，给它设置一个有效时间。但是一些 API Tokens 是永久有效(`ttl=0`)，除非删除它们，否则永不失效，并且 API Tokens 不会因更改用户密码而失效。您可以通过删除 API Tokens 或禁用用户来禁用它们。",
  "source": "@site/docs/rancher2/api/api-tokens/_index.md",
  "permalink": "/docs/rancher2/api/api-tokens/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/api/api-tokens/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar": "rancher2",
  "previous": {
    "title": "使用说明",
    "permalink": "/docs/rancher2/api/_index"
  },
  "next": {
    "title": "原生 Kubernetes API",
    "permalink": "/docs/rancher2/api/api-resources/_index"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: '删除 API Token',
  id: '删除-api-token',
  children: [{
    value: '在 Kubeconfig Tokens 上设置 TTL',
    id: '在-kubeconfig-tokens-上设置-ttl',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`我们建议您在创建 API KEY 时，给它设置一个有效时间。有些 API Tokens 是永久有效(`}<inlineCode parentName="p">{`ttl=0`}</inlineCode>{`)，除非删除它们，否则永不失效，并且 API Tokens 不会因更改用户密码而失效。`}</p>
    <p>{`您可以通过删除 API Tokens 或禁用用户来禁用它们。`}</p>
    <h2 {...{
      "id": "删除-api-token"
    }}>{`删除 API Token`}</h2>
    <p>{`删除 API Tokens 的操作步骤如下：`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`访问 Rancher API 视图中的所有 Tokens 列表：`}<inlineCode parentName="p">{`https://<Rancher-Server-IP>/v3/tokens`}</inlineCode>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`通过要删除的 Tokens ID 访问，例如：`}<inlineCode parentName="p">{`https://<Rancher-Server-IP>/v3/tokens/kubectl-shell-user-vqkqt`}</inlineCode>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`单击`}<strong parentName="p">{`删除`}</strong>{`。`}</p>
      </li>
    </ol>
    <p>{`以下是用`}<inlineCode parentName="p">{`ttl=0`}</inlineCode>{`生成的 Tokens 完整列表：`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Token`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`kubeconfig-*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Kubeconfig Token`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`kubectl-shell-*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`UI 中执行 `}<inlineCode parentName="td">{`kubectl`}</inlineCode>{` 命令的 Token`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`agent-*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Agent 使用的 Token`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`compose-token-*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`compose 使用的 Token`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`helm-token-*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Helm chart 使用的 Token`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`*-pipeline*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`项目流水线使用的 Token`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`telemetry-*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Telemetry Token`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`drain-node-*`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Token for drain (我们使用 `}<inlineCode parentName="td">{`kubectl`}</inlineCode>{` 来执行驱逐操作，因为没有原生 Kubernetes API 支持这个操作)`}</td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "在-kubeconfig-tokens-上设置-ttl"
    }}>{`在 Kubeconfig Tokens 上设置 TTL`}</h3>
    <p><em parentName="p">{`v2.4.6+ 可用`}</em></p>
    <p>{`从 Rancher v2.4.6 开始，管理员可以对 Kubeconfig 令牌设置全局 TTL。一旦令牌过期，kubectl 命令将要求用户对 Rancher 进行验证。`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`在 Rancher API 视图\`https://<Rancher-Server-IP/v3/settings/kubeconfig-generate-token>中禁用 kubeconfig-generate-token 设置。该设置指示 Rancher 不再在用户单击下载 kubeconfig 文件时自动生成令牌。kubeconfig 文件现在将提供登录 Rancher 的命令。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`编辑该设置，并将该值设置为 `}<inlineCode parentName="p">{`false`}</inlineCode>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`在 Rancher API 视图中进入设置 kubeconfig-token-ttl-minutes，网址为`}<inlineCode parentName="p">{`https://<Rancher-Server-IP/v3/settings/kubeconfig-token-ttl-minutes`}</inlineCode>{`。默认情况下，kubeconfig-token-ttl-minutes 为 960 分钟（16 小时）。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`编辑设置并将其设置为所需的持续时间，单位为分钟。
`}<strong parentName="p"><em parentName="strong">{`注意：`}</em></strong>{`这个值不能超过 API 令牌的最大长度。(`}<inlineCode parentName="p">{`https://<Rancher-Server-IP/v3/settings/auth-token-max-ttl-minutes`}</inlineCode>{`)。在 Rancher v2.4.6 中，auth-token-max-ttl-minutes 默认设置为 1440（24 小时）。从 Rancher v2.4.7 开始，auth-token-max-ttl-minutes 将默认为 0，允许令牌永不过期，与 v2.4.5 类似。`}</p>
      </li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
