export const rightToc = [{
  value: '基于 Helm chart 部署',
  id: '基于-helm-chart-部署',
  children: [{
    value: '添加 Octopus Helm 仓库',
    id: '添加-octopus-helm-仓库',
    children: []
  }, {
    value: '安装应用',
    id: '安装应用',
    children: []
  }, {
    value: 'Helm Chart 和 Octopus 支持',
    id: 'helm-chart-和-octopus-支持',
    children: []
