export const rightToc = [{
  value: '重要说明',
  id: '重要说明',
  children: []
}, {
  value: '实验性功能',
  id: '实验性功能',
  children: []
}, {
  value: '自 v2.3.0 以来修复的主要问题',
  id: '自-v230-以来修复的主要问题',
  children: []
}, {
  value: '其他说明',
  id: '其他说明',
  children: [{
    value: '离线安装和升级',
    id: '离线安装和升级',
