  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '系统镜像'
};
export const metadata = {
  "id": "rke/config-options/system-images/_index",
  "title": "系统镜像",
  "description": "## 概述",
  "source": "@site/docs/rke/config-options/system-images/_index.md",
  "permalink": "/docs/rke/config-options/system-images/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rke/config-options/system-images/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '概述',
  id: '概述',
  children: []
}, {
  value: '离线安装',
  id: '离线安装',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "概述"
    }}>{`概述`}</h2>
    <p>{`RKE 在部署 Kubernetes 的时候，会从镜像仓库中拉镜像。这些镜像被用作 Kubernetes 系统组件，同时也帮助部署这些系统组件。`}</p>
    <p>{`从`}<inlineCode parentName="p">{`v0.1.6`}</inlineCode>{`开始，多个系统镜像的功能被整合到一个`}<inlineCode parentName="p">{`rancher/rke-tools`}</inlineCode>{`镜像中，以简化和加快部署过程。`}</p>
    <p>{`您可以配置`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/add-ons/network-plugins/_index"
      }}>{`网络插件`}</a>{`。`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/add-ons/ingress-controllers/_index"
      }}>{`ingress controller`}</a>{`和`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/add-ons/dns/_index"
      }}>{`dns provider`}</a>{`以及这些附加组件的选项分别在`}<inlineCode parentName="p">{`cluster. yml`}</inlineCode>{`中，以及这些附加组件的选项。`}</p>
    <p>{`下面是通过 RKE 部署 Kubernetes 所使用的系统镜像列表的一个例子。Kubernetes 的默认版本是与特定版本的系统镜像绑定的。`}</p>
    <ul>
      <li parentName="ul">
        <p parentName="li">{`对于 RKE v0.2.x 及以下版本，版本和系统镜像版本位于：`}<a parentName="p" {...{
            "href": "https://github.com/rancher/types/blob/release/v2.2/apis/management.cattle.io/v3/k8s_defaults.go"
          }}>{`https://github.com/rancher/types/blob/release/v2.2/apis/management.cattle.io/v3/k8s_defaults.go`}</a></p>
      </li>
      <li parentName="ul">
        <p parentName="li">{`对于 RKE v0.3.0 及以上版本，版本和系统镜像版本位于：`}<a parentName="p" {...{
            "href": "https://github.com/rancher/kontainer-driver-metadata/blob/master/rke/k8s_rke_system_images.go"
          }}>{`https://github.com/rancher/kontainer-driver-metadata/blob/master/rke/k8s_rke_system_images.go`}</a></p>
      </li>
    </ul>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{`随着 RKE 版本的发布，这些镜像的标签将不再是最新的。这个列表是针对`}<inlineCode parentName="p">{`v1.10.3-rancher2`}</inlineCode>{`的。`}</p>
    </blockquote>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`system_images:
  etcd: rancher/coreos-etcd:v3.2.24
  alpine: rancher/rke-tools:v0.1.24
  nginx_proxy: rancher/rke-tools:v0.1.24
  cert_downloader: rancher/rke-tools:v0.1.24
  kubernetes: rancher/hyperkube:v1.13.1-rancher1
  kubernetes_services_sidecar: rancher/rke-tools:v0.1.24
  pod_infra_container: rancher/pause-amd64:3.1

  # kube-dns images
  kubedns: rancher/k8s-dns-kube-dns-amd64:1.15.0
  dnsmasq: rancher/k8s-dns-dnsmasq-nanny-amd64:1.15.0
  kubedns_sidecar: rancher/k8s-dns-sidecar-amd64:1.15.0
  kubedns_autoscaler: rancher/cluster-proportional-autoscaler-amd64:1.0.0

  # CoreDNS images
  coredns: coredns/coredns:1.2.6
  coredns_autoscaler: rancher/cluster-proportional-autoscaler-amd64:1.0.0

  # Flannel images
  flannel: rancher/coreos-flannel:v0.10.0
  flannel_cni: rancher/coreos-flannel-cni:v0.3.0

  # Calico images
  calico_node: rancher/calico-node:v3.4.0
  calico_cni: rancher/calico-cni:v3.4.0
  calico_controllers: ""
  calico_ctl: rancher/calico-ctl:v2.0.0

  # Canal images
  canal_node: rancher/calico-node:v3.4.0
  canal_cni: rancher/calico-cni:v3.4.0
  canal_flannel: rancher/coreos-flannel:v0.10.0

  # Weave images
  weave_node: weaveworks/weave-kube:2.5.0
  weave_cni: weaveworks/weave-npc:2.5.0

  # Ingress controller images
  ingress: rancher/nginx-ingress-controller:0.21.0-rancher1
  ingress_backend: rancher/nginx-ingress-controller-defaultbackend:1.4

  # Metrics server image
  metrics_server: rancher/metrics-server-amd64:v0.3.1
`}</code></pre>
    <p>{`Prior to `}<inlineCode parentName="p">{`v0.1.6`}</inlineCode>{`, instead of using the `}<inlineCode parentName="p">{`rancher/rke-tools`}</inlineCode>{` image, we used the following images:`}</p>
    <p>{`在 "v0.1.6 "之前，我们不使用`}<inlineCode parentName="p">{`rancher/rke-tools`}</inlineCode>{`镜像，而是使用以下镜像：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`system_images:
  alpine: alpine:latest
  nginx_proxy: rancher/rke-nginx-proxy:v0.1.1
  cert_downloader: rancher/rke-cert-deployer:v0.1.1
  kubernetes_services_sidecar: rancher/rke-service-sidekick:v0.1.0
`}</code></pre>
    <h2 {...{
      "id": "离线安装"
    }}>{`离线安装`}</h2>
    <p>{`如果您处于离线环境，不能访问`}<inlineCode parentName="p">{`docker.io`}</inlineCode>{`，您将需要在您的集群配置文件中设置您的`}<a parentName="p" {...{
        "href": "/docs/rke/config-options/private-registries/_index"
      }}>{`私有镜像仓库`}</a>{`。设置好私有镜像仓库后，您需要更新这些镜像，以便从您的私有镜像仓库中提取最新版本的镜像。`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
