  id: '5替换containerd二进制文件',
  children: []
}, {
  value: '6.重启docker 检查containerd版本是否替换成功',
  id: '6重启docker-检查containerd版本是否替换成功',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "二进制方式升级containerd版本"
    }}>{`二进制方式升级containerd版本`}</h1>
    <blockquote>
      <p parentName="blockquote">{`当前环境版本信息
docker：19.03.9
升级containerd：1.3.9 —> 1.4.3`}</p>
    </blockquote>
    <h3 {...{
      "id": "1-github下载二进制containerd"
    }}>{`1. github下载二进制containerd`}</h3>
    <p>{`链接： `}<a parentName="p" {...{
        "href": "https://github.com/containerd/containerd/releases/tag/v1.4.3"
      }}>{`https://github.com/containerd/containerd/releases/tag/v1.4.3`}</a>{`
`}<img parentName="p" {...{
        "src": "https://img-blog.csdnimg.cn/img_convert/d0b9ec25fe30c07395a45440b7189e67.png",
        "alt": null
      }}></img></p>
    <h3 {...{
      "id": "2解压containerd"
    }}>{`2.解压containerd`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`tar -zxvf containerd-1.4.3-linux-amd64.tar.gz 
`}</code></pre>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E4%BA%8C%E8%BF%9B%E5%88%B6%E6%96%B9%E5%BC%8F%E5%8D%87%E7%BA%A7containerd%E7%89%88%E6%9C%AC/2.jpg",
        "alt": null
      }}></img></p>
    <h3 {...{
      "id": "3检查当前containerd版本"
    }}>{`3.检查当前containerd版本`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`docker info 
containerd -v

`}</code></pre>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E4%BA%8C%E8%BF%9B%E5%88%B6%E6%96%B9%E5%BC%8F%E5%8D%87%E7%BA%A7containerd%E7%89%88%E6%9C%AC/3.jpg",
        "alt": null
      }}></img></p>
    <h3 {...{
      "id": "4暂停docker"
    }}>{`4.暂停docker`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`systemctl stop docker
`}</code></pre>
    <h3 {...{
      "id": "5替换containerd二进制文件"
    }}>{`5.替换containerd二进制文件`}</h3>
    <p>{`需要替换的二进制文件有5个`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`containerd  containerd-shim  containerd-shim-runc-v1  containerd-shim-runc-v2 ctr
`}</code></pre>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E4%BA%8C%E8%BF%9B%E5%88%B6%E6%96%B9%E5%BC%8F%E5%8D%87%E7%BA%A7containerd%E7%89%88%E6%9C%AC/4.jpg",
        "alt": null
      }}></img></p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`cp containerd /usr/bin/containerd
cp containerd-shim /usr/bin/containerd-shim
cp containerd-shim-runc-v1 /usr/bin/containerd-shim-runc-v1
cp containerd-shim-runc-v2 /usr/bin/containerd-shim-runc-v2
cp ctr /usr/bin/ctr
`}</code></pre>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E4%BA%8C%E8%BF%9B%E5%88%B6%E6%96%B9%E5%BC%8F%E5%8D%87%E7%BA%A7containerd%E7%89%88%E6%9C%AC/5.jpg",
        "alt": null
      }}></img></p>
    <h3 {...{
      "id": "6重启docker-检查containerd版本是否替换成功"
    }}>{`6.重启docker 检查containerd版本是否替换成功`}</h3>
    <p><img parentName="p" {...{
        "src": "https://ivanwz.oss-cn-shenzhen.aliyuncs.com/md/%E4%BA%8C%E8%BF%9B%E5%88%B6%E6%96%B9%E5%BC%8F%E5%8D%87%E7%BA%A7containerd%E7%89%88%E6%9C%AC/6.jpg",
        "alt": null
      }}></img></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
