  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '审计日志'
};
export const metadata = {
  "id": "rke/config-options/audit-log/_index",
  "title": "审计日志",
  "description": "## 概述",
  "source": "@site/docs/rke/config-options/audit-log/_index.md",
  "permalink": "/docs/rke/config-options/audit-log/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rke/config-options/audit-log/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: '概述',
  id: '概述',
  children: []
}, {
  value: '默认启用',
  id: '默认启用',
  children: []
}, {
  value: 'YAML 配置示例',
  id: 'yaml-配置示例',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "概述"
    }}>{`概述`}</h2>
    <p>{`Kubernetes 审计提供了关于集群的安全相关的时间顺序记录集。Kube-apiserver 执行审计，每个请求都会产生一个事件，然后根据一定的策略进行预处理，并写入后端。策略决定了记录的内容，而后端则会将记录持久化。`}</p>
    <p>{`为了遵守 CIS（Center for Internet Security）Kubernetes Benchmark，您需要配置审计日志。`}</p>
    <p>{`有关配置细节，请参考`}<a parentName="p" {...{
        "href": "https://kubernetes.io/docs/tasks/debug-application-cluster/audit/"
      }}>{`Kubernetes 官方文档`}</a>{`。`}</p>
    <h2 {...{
      "id": "默认启用"
    }}>{`默认启用`}</h2>
    <p>{`在 RKE v1.1.0+中，使用特定 Kubernetes 版本时，审计日志是默认启用的。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`RKE 版本`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Kubernetes 版本`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`是否默认启用审计日志`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.1.0+`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.17.4+`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`是`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.1.0 +`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.16.8+`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`是`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.1.0 +`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`v1.15.11+`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`是`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "yaml-配置示例"
    }}>{`YAML 配置示例`}</h2>
    <p>{`在`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`中使用以下配置可以默认启用审计日志：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`services:
  kube-api:
    audit_log:
      enabled: true
`}</code></pre>
    <p>{`启用审计日志后，可以在`}<inlineCode parentName="p">{`/etc/kubernetes/audit-policy.yaml`}</inlineCode>{`中看到默认值（在 RKE v1.1.0 之前，路径是`}<inlineCode parentName="p">{`/etc/kubernetes/audit.yaml`}</inlineCode>{`）。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`# 最简单的审计日志配置：采集事件元数据
---
rules:
  - level: Metadata
`}</code></pre>
    <p>{`启用审计日志后，还将为审计日志路径、最大年龄、最大备份数量、最大大小（兆字节）和格式设置默认值。请运行以下命令查看默认值：`}</p>
    <pre><code parentName="pre" {...{}}>{`ps -ef | grep kube-apiserver
`}</code></pre>
    <p>{`在 RKE v1.1.0 中，审计日志的默认值为：`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`--audit-log-maxage=5 # 保留旧审计日志文件的最长天数
--audit-log-maxbackup=5 # 保留旧审计日志文件的最大数量
--audit-log-path=/var/log/kube-audit/audit-log.json # 日志后端用于写入审计事件的日志文件路径
--audit-log-maxsize=100 # 审计日志文件在被替换之前的最大大小（MB）
--audit-policy-file=/etc/kubernetes/audit.yaml # 包含您的审计日志规则的文件
--audit-log-format=json # 日志文件格式

`}</code></pre>
    <p>{`您可以使用`}<inlineCode parentName="p">{`configuration`}</inlineCode>{`自定义审计日志。`}</p>
    <p>{`规则策略通过`}<inlineCode parentName="p">{`--audit-policy-file`}</inlineCode>{`或`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`中的`}<inlineCode parentName="p">{`policy`}</inlineCode>{`指令传递给 kube-apiserver。下面是一个例子`}<inlineCode parentName="p">{`cluster.yml`}</inlineCode>{`，其中包含自定义值和嵌套在`}<inlineCode parentName="p">{`configuration`}</inlineCode>{`指令下的审计日志策略。这个审计日志策略示例来自官方`}<a parentName="p" {...{
        "href": "https://kubernetes.io/docs/tasks/debug-application-cluster/audit/#audit-policy"
      }}>{`Kubernetes 文档`}</a>{`。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-yaml"
      }}>{`services:
  kube-api:
    audit_log:
      enabled: true
      configuration:
        max_age: 6
        max_backup: 6
        max_size: 110
        path: /var/log/kube-audit/audit-log.json
        format: json
        policy:
          apiVersion: audit.k8s.io/v1 # 必填
          kind: Policy
          omitStages:
            - "RequestReceived"
          rules:
            # 在RequestResponse级别记录pod变化
            - level: RequestResponse
              resources:
                - group: ""
                  # 资源 "pods "不匹配对pods的任何子资源的请求
                  # 与RBAC策略是一致的
                  resources: ["pods"]
            # 在元数据层记录 "pods/log"、"pods/status"
            - level: Metadata
              resources:
                - group: ""
                  resources: ["pods/log", "pods/status"]

            # 不要将请求记录到名为 "controller-leader "的配置图上
            - level: None
              resources:
                - group: ""
                  resources: ["configmaps"]
                  resourceNames: ["controller-leader"]

            # 不要在端点或服务上记录 "system:keube-proxy "的监视请求
            - level: None
              users: ["system:kube-proxy"]
              verbs: ["watch"]
              resources:
                - group: "" # core API group
                  resources: ["endpoints", "services"]

            # 不要记录对某些非资源URL路径的认证请求
            - level: None
              userGroups: ["system:authenticated"]
              nonResourceURLs:
                - "/api*" # Wildcard matching.
                - "/version"

            # 在kube-system中记录configmap变更的请求体
            - level: Request
              resources:
                - group: "" # core API group
                  resources: ["configmaps"]
              # 此规则只适用于 "kube-system "命名空间中的资源
              # 空字符串""可用于选择非命名间隔的资源
              namespaces: ["kube-system"]

            # 在元数据级别记录所有其他命名空间的configmap和密钥变化
            - level: Metadata
              resources:
                - group: "" # core API group
                  resources: ["secrets", "configmaps"]

            # 在请求层记录核心和扩展的所有其他资源
            - level: Request
              resources:
                - group: "" # core API group
                - group: "extensions" # 不应包括组的版本

            # 一个全面的规则，用于记录元数据级别的所有其他请求
            - level: Metadata
              # 在此规则下，像监控这样的长期运行的请求不会在RequestReceived中产生审计事件
              omitStages:
                - "RequestReceived"
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
