  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '如何选择 Rancher 版本',
	description: '我们建议在生产环境中使用 Rancher 高可用安装，在开发环境和测试环境中使用单节点安装。高可用安装：使用Helm Chart 将 Rancher 安装在 Kubernetes 集群上。请参阅Helm 版本要求，选择安装 Rancher 的 Helm 版本。单节点安装：对于使用 Docker 安装的单节点 Rancher，一般用于开发和测试，Rancher 将以Docker 镜像的形式安装。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'安装指南',
		'资料、参考和高级选项',
		'如何选择 Rancher 版本'
	]
};
export const metadata = {
  "id": "rancher2/installation/options/server-tags/_index",
  "title": "如何选择 Rancher 版本",
  "description": "我们建议在生产环境中使用 Rancher 高可用安装，在开发环境和测试环境中使用单节点安装。高可用安装：使用Helm Chart 将 Rancher 安装在 Kubernetes 集群上。请参阅Helm 版本要求，选择安装 Rancher 的 Helm 版本。单节点安装：对于使用 Docker 安装的单节点 Rancher，一般用于开发和测试，Rancher 将以Docker 镜像的形式安装。",
  "source": "@site/docs/rancher2/installation/options/server-tags/_index.md",
  "permalink": "/docs/rancher2/installation/options/server-tags/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/installation/options/server-tags/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Helm Charts',
  id: 'helm-charts',
  children: [{
    value: 'Helm Chart 仓库',
    id: 'helm-chart-仓库',
    children: []
  }, {
    value: 'Helm Chart 版本',
    id: 'helm-chart-版本',
    children: []
  }, {
    value: '切换到不同 Helm Chart 仓库',
    id: '切换到不同-helm-chart-仓库',
    children: []
  }]
}, {
  value: 'Docker 镜像',
  id: 'docker-镜像',
  children: [{
    value: 'Server 标签',
    id: 'server-标签',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`我们建议在生产环境中使用 Rancher 高可用安装，在开发环境和测试环境中使用单节点安装。`}</p>
    <ul>
      <li parentName="ul">
        <p parentName="li">{`高可用安装：使用 `}<strong parentName="p">{`Helm Chart`}</strong>{` 将 Rancher 安装在 Kubernetes 集群上。请参阅`}<a parentName="p" {...{
            "href": "/docs/rancher2/installation/options/helm-version/_index"
          }}>{`Helm 版本要求`}</a>{`，选择安装 Rancher 的 Helm 版本。`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li">{`单节点安装：对于使用 Docker 安装的单节点 Rancher，一般用于开发和测试，Rancher 将以 `}<strong parentName="p">{`Docker 镜像`}</strong>{`的形式安装。`}</p>
      </li>
    </ul>
    <h2 {...{
      "id": "helm-charts"
    }}>{`Helm Charts`}</h2>
    <p>{`在安装、升级或回滚`}<a parentName="p" {...{
        "href": "/docs/rancher2/installation/k8s-install/_index"
      }}>{`Rancher 高可用`}</a>{`时，您将使用 `}<strong parentName="p">{`Helm Chart`}</strong>{` 在 Kubernetes 集群上对 Rancher 进行操作。因此，在准备安装或升级 Rancher 高可用时，必须添加包含用于安装 Rancher 的 Helm Chart 的 Helm Chart 仓库。`}</p>
    <p>{`请参阅`}<a parentName="p" {...{
        "href": "/docs/rancher2/installation/options/helm-version/_index"
      }}>{`Helm 版本要求`}</a>{`，选择用于安装 Rancher 的 Helm 版本。`}</p>
    <h3 {...{
      "id": "helm-chart-仓库"
    }}>{`Helm Chart 仓库`}</h3>
    <p>{`Rancher 提供了几种不同的 Helm Chart 仓库供您选择。最新版或稳定版的 Rancher Helm Chart 与用于 Docker 安装的 Rancher 的 Docker 镜像标签对应。因此，`}<inlineCode parentName="p">{`rancher-latest`}</inlineCode>{`仓库包含被标记为`}<inlineCode parentName="p">{`rancher/rancher:latest`}</inlineCode>{`的版本。当 Rancher 版本升级到`}<inlineCode parentName="p">{`rancher/rancher:stable`}</inlineCode>{`后，它将被添加到`}<inlineCode parentName="p">{`rancher-stable`}</inlineCode>{`仓库中。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`类别`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`添加仓库命令`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`仓库描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`rancher-latest`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`helm repo add rancher-latest https://releases.rancher.com/server-charts/latest`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`添加最新版本的 Rancher 的 Helm Chart 仓库。我们建议使用此仓库来测试新版本的 Rancher。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`rancher-stable`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`helm repo add rancher-stable https://releases.rancher.com/server-charts/stable`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`添加较旧的，稳定的版本的 Rancher 的 Helm Chart 仓库。我们建议将此仓库用于生产环境。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}>{`rancher-alpha`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`helm repo add rancher-alpha https://releases.rancher.com/server-charts/alpha`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`添加 alpha 版本的 Rancher 的 Helm Chart 仓库，以预览即将发布的版本.不建议在生产环境中使用这些版本。我们不支持从 rancher alpha 仓库中的 chart 升级到任何其他版本 chart。`}</td>
        </tr>
      </tbody>
    </table>
    <p>{`如何选择这些仓库，请参考`}<a parentName="p" {...{
        "href": "#%E5%88%87%E6%8D%A2%E5%88%B0%E4%B8%8D%E5%90%8C-helm-chart-%E4%BB%93%E5%BA%93"
      }}>{`切换到不同 Helm Chart 仓库`}</a>{`。`}</p>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{` `}<inlineCode parentName="p">{`rancher-latest`}</inlineCode>{` 和 `}<inlineCode parentName="p">{`rancher-stable`}</inlineCode>{` Helm Chart 仓库是在 Rancher v2.1.0 后引入的，因此`}<inlineCode parentName="p">{`rancher-stable`}</inlineCode>{`仓库包含一些从来没有被标记为`}<inlineCode parentName="p">{`rancher/rancher:stable`}</inlineCode>{`标签的 Rancher 版本。在 v2.1.0 之前标记为`}<inlineCode parentName="p">{`rancher/rancher:stable`}</inlineCode>{`的 Rancher 版本是 v2.0.4，v2.0.6，v2.0.8。在 v2.1.0 版本之后，`}<inlineCode parentName="p">{`rancher-stable`}</inlineCode>{`仓库中的所有 charts 将与标记为`}<inlineCode parentName="p">{`stable`}</inlineCode>{`的 Rancher 版本对应。`}</p>
    </blockquote>
    <h3 {...{
      "id": "helm-chart-版本"
    }}>{`Helm Chart 版本`}</h3>
    <p>{`Rancher Helm Chart 版本必须匹配 Rancher 版本（即`}<inlineCode parentName="p">{`appVersion`}</inlineCode>{`）。`}</p>
    <p>{`有一些 Rancher v2.1.x 版本，Helm Chart 版本使用的版本号是构建版本号，即`}<inlineCode parentName="p">{`yyyy.mm.<build-number>`}</inlineCode>{`，这些 charts 已经被等效的 Rancher 版本替换，并且不再可用。`}</p>
    <h3 {...{
      "id": "切换到不同-helm-chart-仓库"
    }}>{`切换到不同 Helm Chart 仓库`}</h3>
    <p>{`安装 Rancher 之后，如果想要修改安装 Rancher 的 Helm Chart 仓库，需要执行以下步骤。`}</p>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{` 由于 rancher-alpha 仓库只包含 alpha 版本 charts，因此不支持在 rancher alpha 仓库和 rancher stable 或 rancher latest 仓库之间切换以进行升级。`}</p>
    </blockquote>
    <p>{`请替换命令中的`}<inlineCode parentName="p">{`<CHART_REPO>`}</inlineCode>{`，替换为`}<inlineCode parentName="p">{`latest`}</inlineCode>{`、`}<inlineCode parentName="p">{`stable`}</inlineCode>{`或`}<inlineCode parentName="p">{`alpha`}</inlineCode>{`。`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`latest`}</inlineCode>{`：推荐在尝试新功能时使用。`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`stable`}</inlineCode>{`：推荐生产环境中使用。（推荐）`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`alpha`}</inlineCode>{`：未来版本的实验性预览。`}</li>
    </ul>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`输入`}<inlineCode parentName="p">{`helm repo list`}</inlineCode>{`命令，列出当前 Helm Chart 仓库。`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-plain"
          }}>{`helm repo list

NAME                    URL
stable                  https://kubernetes-charts.storage.googleapis.com
rancher-<CHART_REPO>    https://releases.rancher.com/server-charts/<CHART_REPO>
`}</code></pre>
      </li>
      <li parentName="ol">
        <p parentName="li">{`输入`}<inlineCode parentName="p">{`helm repo remove rancher-<CHART_REPO>`}</inlineCode>{`命令移除您安装 Rancher 时用的 Helm Chart 仓库，是`}<inlineCode parentName="p">{`rancher-stable`}</inlineCode>{` 还是 `}<inlineCode parentName="p">{`rancher-latest`}</inlineCode>{`仓库取决于您初始安装时选择的是哪个库。`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-plain"
          }}>{`helm repo remove rancher-<CHART_REPO>
`}</code></pre>
      </li>
      <li parentName="ol">
        <p parentName="li">{`输入`}<inlineCode parentName="p">{`helm repo add rancher-<CHART_REPO> https://releases.rancher.com/server-charts/<CHART_REPO>`}</inlineCode>{`命令，添加安装 Rancher 所需要的 Helm Chart 仓库。`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-plain"
          }}>{`helm repo add rancher-<CHART_REPO> https://releases.rancher.com/server-charts/<CHART_REPO>
`}</code></pre>
      </li>
      <li parentName="ol">
        <p parentName="li">{`继续按照这个步骤从新的 Helm Chart 仓库`}<a parentName="p" {...{
            "href": "/docs/rancher2/upgrades/upgrades/ha/_index"
          }}>{`升级 Rancher`}</a>{`。`}</p>
      </li>
    </ol>
    <h2 {...{
      "id": "docker-镜像"
    }}>{`Docker 镜像`}</h2>
    <p>{`在执行`}<a parentName="p" {...{
        "href": "/docs/rancher2/installation/other-installation-methods/single-node-docker/_index/"
      }}>{`Docker 单节点安装`}</a>{`升级或回滚时，您可以使用`}<em parentName="p">{`标签`}</em>{`安装特定版本的 Rancher。`}</p>
    <h3 {...{
      "id": "server-标签"
    }}>{`Server 标签`}</h3>
    <p>{`Rancher Server 作为 Docker 镜像分发，其中附加了标签。您可以在输入用于部署 Rancher 的命令时指定此标签。请记住，如果您使用的标签没有显式指定版本（例如`}<inlineCode parentName="p">{`latest`}</inlineCode>{`或`}<inlineCode parentName="p">{`stable`}</inlineCode>{`），则必须显式拉取该镜像标签的新版本。否则，主机上缓存的镜像会被使用。`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`标签`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`描述`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`rancher/rancher:latest`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`我们的最新版本。这些构建通过了我们的 CI 自动化验证。我们不建议将这些版本用于生产环境。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`rancher/rancher:stable`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`我们最新的稳定版本。建议将此标签用于生产。`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`rancher/rancher:<v2.X.X>`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`您可以使用标签来安装特定版本的 Rancher。您到 DockerHub 上查看有哪些可用的版本`}</td>
        </tr>
      </tbody>
    </table>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong></p>
      <ul parentName="blockquote">
        <li parentName="ul"><inlineCode parentName="li">{`master`}</inlineCode>{`标签或带有`}<inlineCode parentName="li">{`-rc`}</inlineCode>{`或其他后缀的标签都是给 Rancher 测试团队验证用的版本。您不应该使用这些标签的版本，因为这些构建不受官方支持。`}</li>
        <li parentName="ul">{`想要安装 alpha 版本体验吗？使用我们`}<a parentName="li" {...{
            "href": "https://forums.rancher.com/c/announcements"
          }}>{`公告页面`}</a>{` (例如 `}<inlineCode parentName="li">{`v2.2.0-alpha1`}</inlineCode>{`)上列出的某个 alpha 标签进行安装。注意：Alpha 版本无法升级到任何其他版本或从任何其他版本升级。`}</li>
      </ul>
    </blockquote>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
