  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '配置 Rancher 使用 AD FS',
	description: '完成在 Microsoft AD FS 中配置 Rancher 对接后，将您的 AD FS 信息输入 Rancher，以允许 Rancher 通过 AD FS 进行身份验证。',
	keywords: [
		'rancher 2.0中文文档',
		'rancher 2.x 中文文档',
		'rancher中文',
		'rancher 2.0中文',
		'rancher2',
		'rancher教程',
		'rancher中国',
		'rancher 2.0',
		'rancher2.0 中文教程',
		'系统管理员指南',
		'登录认证',
		'AD FS（SAML）',
		'配置 Rancher 使用 AD FS'
	]
};
export const metadata = {
  "id": "rancher2/admin-settings/authentication/microsoft-adfs/rancher-adfs-setup/_index",
  "title": "配置 Rancher 使用 AD FS",
  "description": "完成在 Microsoft AD FS 中配置 Rancher 对接后，将您的 AD FS 信息输入 Rancher，以允许 Rancher 通过 AD FS 进行身份验证。",
  "source": "@site/docs/rancher2/admin-settings/authentication/microsoft-adfs/rancher-adfs-setup/_index.md",
  "permalink": "/docs/rancher2/admin-settings/authentication/microsoft-adfs/rancher-adfs-setup/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/rancher2/admin-settings/authentication/microsoft-adfs/rancher-adfs-setup/_index.md",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><em parentName="p">{`自 v2.0.7 版本起可用`}</em></p>
    <p>{`完成`}<a parentName="p" {...{
        "href": "/docs/rancher2/admin-settings/authentication/microsoft-adfs/microsoft-adfs-setup/_index"
      }}>{`在 Microsoft AD FS 中配置 Rancher 对接`}</a>{`后，将您的 AD FS 信息输入 Rancher，以允许 Rancher 通过 AD FS 进行身份验证。`}</p>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`有关配置 AD FS 服务器的重要说明：`}</strong></p>
      <ul parentName="blockquote">
        <li parentName="ul">{`SAML 2.0 WebSSO 协议服务 URL 为: `}<inlineCode parentName="li">{`https://<RANCHER_SERVER>/v1-saml/adfs/saml/acs`}</inlineCode></li>
        <li parentName="ul">{`依赖方信任标识符 URL 为: `}<inlineCode parentName="li">{`https://<RANCHER_SERVER>/v1-saml/adfs/saml/metadata`}</inlineCode></li>
        <li parentName="ul">{`您必须从 AD FS 服务器导出`}<inlineCode parentName="li">{`federationmetadata.xml`}</inlineCode>{`文件。可以在以下位置找到这个文件：`}<inlineCode parentName="li">{`https://<AD_SERVER>/federationmetadata/2007-06/federationmetadata.xml`}</inlineCode></li>
      </ul>
    </blockquote>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`在`}<strong parentName="p">{`全局`}</strong>{`视图中，选择 `}<strong parentName="p">{`安全 > 认证`}</strong>{` 菜单。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`选择 `}<strong parentName="p">{`AD FS`}</strong>{`。`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`完成 `}<strong parentName="p">{`配置 AD FS 帐户`}</strong>{` 表单。Microsoft AD FS 允许您指定现有的 Active Directory（AD）服务器。以下示例描述了如何将 AD 属性映射到 Rancher 中的字段。`}</p>
        <table parentName="li">
          <thead parentName="table">
            <tr parentName="thead">
              <th parentName="tr" {...{
                "align": "left"
              }}>{`字段名`}</th>
              <th parentName="tr" {...{
                "align": "left"
              }}>{`描述`}</th>
            </tr>
          </thead>
          <tbody parentName="table">
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": "left"
              }}>{`显示名称`}</td>
              <td parentName="tr" {...{
                "align": "left"
              }}>{`包含用户显示名称的 AD 属性. `}<br /><br />{`例: `}<inlineCode parentName="td">{`http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name`}</inlineCode></td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": "left"
              }}>{`用户名`}</td>
              <td parentName="tr" {...{
                "align": "left"
              }}>{`包含用户名/给定名称的 AD 属性。 `}<br /><br />{`例: `}<inlineCode parentName="td">{`http://schemas.xmlsoap.org/ws/2005/05/identity/claims/givenname`}</inlineCode></td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": "left"
              }}>{`UID`}</td>
              <td parentName="tr" {...{
                "align": "left"
              }}>{`每个用户唯一的 AD 属性。 `}<br /><br />{`例: `}<inlineCode parentName="td">{`http://schemas.xmlsoap.org/ws/2005/05/identity/claims/upn`}</inlineCode></td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": "left"
              }}>{`组`}</td>
              <td parentName="tr" {...{
                "align": "left"
              }}>{`进行输入以管理组成员身份。 `}<br /><br />{`例: `}<inlineCode parentName="td">{`http://schemas.xmlsoap.org/claims/Group`}</inlineCode></td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": "left"
              }}>{`Rancher API 地址`}</td>
              <td parentName="tr" {...{
                "align": "left"
              }}>{`Rancher Server 的 URL。`}</td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": "left"
              }}>{`密钥 / 证书`}</td>
              <td parentName="tr" {...{
                "align": "left"
              }}>{`这是用于在 Rancher 和您的 AD FS 之间创建安全 shell 的密钥证书对。确保将通用名称（CN）设置为 Rancher Server URL。`}</td>
            </tr>
            <tr parentName="tbody">
              <td parentName="tr" {...{
                "align": "left"
              }}>{`元数据 XML`}</td>
              <td parentName="tr" {...{
                "align": "left"
              }}>{`从 AD FS 服务器导出的`}<inlineCode parentName="td">{`federationmetadata.xml`}</inlineCode>{`文件。 `}<br /><br />{`您可以在这里找到此文件 `}<inlineCode parentName="td">{`https://<AD_SERVER>/federationmetadata/2007-06/federationmetadata.xml`}</inlineCode>{`。`}</td>
            </tr>
          </tbody>
        </table>
        <blockquote parentName="li">
          <p parentName="blockquote"><strong parentName="p">{`提示：`}</strong>{` 您可以使用 openssl 命令生成证书。例如：`}</p>
          <pre parentName="blockquote"><code parentName="pre" {...{}}>{`   openssl req -x509 -newkey rsa:2048 -keyout myservice.key -out myservice.cert -days 365 -nodes -subj "/CN=myservice.example.com"
`}</code></pre>
        </blockquote>
      </li>
      <li parentName="ol">
        <p parentName="li">{`完成 `}<strong parentName="p">{`配置 AD FS 帐户`}</strong>{` 表单后, 单击 `}<strong parentName="p">{`使用 AD FS 进行身份验证`}</strong>{`按钮。`}</p>
        <p parentName="li">{`Rancher 将您重定向到 AD FS 登录页面。输入通过 Microsoft AD FS 进行身份验证的凭据，以验证 Rancher AD FS 配置。`}</p>
      </li>
    </ol>
    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`注意：`}</strong>{` 您可能必须禁用弹出窗口阻止程序才能查看 AD FS 登录页面。`}</p>
    </blockquote>
    <p><strong parentName="p">{`结果：`}</strong>{` Rancher 被配置为与 AD FS 一起使用。您的用户现在可以使用其 AD FS 登录名登录 Rancher。`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
