  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Helm',
	description: 'Helm 是 Kubernetes 的首选包管理工具。Helm 图表为 Kubernetes YAML 清单文件提供了模板化语法。通过 Helm，我们可以创建可配置的部署，而不仅仅是使用静态文件。',
	keywords: [
		'k3s中文文档',
		'k3s 中文文档',
		'k3s中文',
		'k3s 中文',
		'k3s',
		'k3s教程',
		'k3s中国',
		'rancher',
		'k3s 中文教程',
		'K3s常见问题'
	]
};
export const metadata = {
  "id": "k3s/helm/_index",
  "title": "Helm",
  "description": "Helm 是 Kubernetes 的首选包管理工具。Helm 图表为 Kubernetes YAML 清单文件提供了模板化语法。通过 Helm，我们可以创建可配置的部署，而不仅仅是使用静态文件。",
  "source": "@site/docs/k3s/helm/_index.md",
  "permalink": "/docs/k3s/helm/_index",
  "editUrl": "https://github.com/cnrancher/docs-rancher2/edit/master/docs/k3s/helm/_index.md",
