export const rightToc = [{
  value: '部署 manifests 和 Helm charts',
  id: '部署-manifests-和-helm-charts',
  children: []
}, {
  value: '使用 Helm CRD',
  id: '使用-helm-crd',
  children: [{
    value: 'HelmChart 字段定义',
    id: 'helmchart-字段定义',
    children: []
  }]
}, {
  value: '使用 HelmChartConfig 自定义打包的组件',
  id: '使用-helmchartconfig-自定义打包的组件',
  children: []
}, {
  value: '从 Helm v2 升级',
